import React, { useState } from 'react';
import NavBar from "../NavBar/Navbar";
import axios from 'axios'; // Import Axios for API requests
import './MiloChatbot.css';

const MiloChatbot = () => {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSendMessage = async () => {
    if (!userInput.trim()) return;

    // Add user message to chat
    setMessages((prevMessages) => [
      ...prevMessages,
      { text: userInput, sender: 'user' },
    ]);

    setIsLoading(true);
    setUserInput('');

    try {
      // Send user input to Flask API
      const response = await axios.post('http://127.0.0.1:5000/chat', {
        message: userInput,
      });

      // Add bot response to chat
      setMessages((prevMessages) => [
        ...prevMessages,
        { text: response.data.response, sender: 'bot' },
      ]);
    } catch (error) {
      console.error("Error connecting to chatbot API:", error);
      setMessages((prevMessages) => [
        ...prevMessages,
        { text: "Sorry, something went wrong. Please try again later.", sender: 'bot' },
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="navbar-container">
      <NavBar />
      <div className="chat-container">
        <div className="chat-history">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`chat-bubble ${message.sender === 'user' ? 'user-bubble' : 'bot-bubble'}`}
            >
              {message.text}
            </div>
          ))}
          {isLoading && (
            <div className="chat-bubble bot-bubble">...</div>
          )}
        </div>

        <div className="chat-input-container">
          <input
            type="text"
            className="chat-input"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            placeholder="Type a message..."
          />
          <button
            className="send-button"
            onClick={handleSendMessage}
            disabled={isLoading}
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default MiloChatbot;
