import React, { useState, useEffect } from "react";
import "./Restaurants.css";
import NavBar from "../NavBar/Navbar";

const Restaurants = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    // Fetch restaurant data
    fetch("./new_restaurant_db.restaurants_data.json")
      .then((res) => res.json())
      .then((data) => setRestaurants(data))
      .catch((err) => console.error("Error loading restaurant data:", err));

    // Fetch review data
    fetch("./new_restaurant_db.restaurants_reviews.json")
      .then((res) => res.json())
      .then((data) => setReviews(data))
      .catch((err) => console.error("Error loading reviews:", err));
  }, []);

  // Function to get rating by matching restaurant name
  const getRating = (name) => {
    const review = reviews.find((r) => r.restaurant_name === name);
    return review ? review.rating : "No rating available";
  };

  // Group restaurants by city
  const groupedByCity = restaurants.reduce((acc, restaurant) => {
    const city = restaurant.city || "Unknown";
    if (!acc[city]) {
      acc[city] = [];
    }
    acc[city].push(restaurant);
    return acc;
  }, {});

  return (
    <div className="restaurants-container">
      <NavBar />
      <h1>Restaurants</h1>
      {Object.entries(groupedByCity).map(([city, cityRestaurants]) => (
        <div key={city} className="city-section">
          <h2>{city}</h2>
          <div className="restaurants-row">
            {cityRestaurants.slice(0, 5).map((restaurant, index) => (
              <div key={index} className="restaurant-card">
                <img src={restaurant.image_url} alt={restaurant.name} className="restaurant-image" />
                <h3>{restaurant.name}</h3>
                <p>Cuisine: {restaurant.cuisine}</p>
                <p>Location: {restaurant.location}</p>
                <p>Rating: {getRating(restaurant.name)}</p>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Restaurants;
