import React, { useState, useEffect } from "react";
import "./Profile.css"; // Ensure styles match login/signup
import Navbar from '../NavBar/Navbar';
import axios from "axios"; // Use axios for API calls

const Profile = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [user, setUser] = useState({});
  const [itineraries, setItineraries] = useState([]);
  const [challenges, setChallenges] = useState([]);
  const [vouchers, setVouchers] = useState([]);
  const [totalPoints, setTotalPoints] = useState(0);

  // Fetch user data, itineraries, and challenges from the backend
  useEffect(() => {
    const fetchData = async () => {
      try {
        const userEmail = localStorage.getItem("userEmail"); // Assuming you store the user's email in localStorage after login

        // Fetch user details
        const userResponse = await axios.get(`/api/user/details?email=${userEmail}`);
        setUser(userResponse.data);

        // Fetch itineraries
        const itinerariesResponse = await axios.get(`/api/user/itineraries?email=${userEmail}`);
        setItineraries(itinerariesResponse.data);

        // Fetch challenges
        const challengesResponse = await axios.get(`/api/user/challenges?email=${userEmail}`);
        setChallenges(challengesResponse.data);

        // Fetch total points
        const pointsResponse = await axios.get(`/api/user/points?email=${userEmail}`);
        setTotalPoints(pointsResponse.data.points);

        // Fetch vouchers (hardcoded or from backend)
        const vouchersResponse = await axios.get("/api/vouchers"); // Replace with your API endpoint
        setVouchers(vouchersResponse.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const handleEditToggle = () => setIsEditing(!isEditing);

  const handleInputChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleRedeem = (index) => {
    if (totalPoints >= vouchers[index].requiredPoints) {
      const updatedVouchers = [...vouchers];
      updatedVouchers[index].isRedeemed = true;
      setVouchers(updatedVouchers);
      alert("🎉 Voucher Redeemed!");
    } else {
      alert("❌ Not enough points.");
    }
  };

  return (
    <div className="profile-container">
      <Navbar />
      <div className="profile-content">
        <h2 className="profile-title">User Profile</h2>

        {/* Personal Details Section */}
        <div className="profile-section">
          <h3>Personal Details</h3>
          <div className="profile-details">
            {Object.keys(user).map((key) => (
              <div className="profile-field" key={key}>
                <label>{key.replace(/([A-Z])/g, " $1")}:</label>
                <input
                  type="text"
                  name={key}
                  value={user[key] || ""}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                  className="profile-input"
                />
              </div>
            ))}
            <button className="edit-btn" onClick={handleEditToggle}>
              {isEditing ? "Save" : "Edit"}
            </button>
          </div>
        </div>

        {/* Itineraries Section */}
        <div className="profile-section">
          <h3>Past Itineraries</h3>
          <div className="itineraries">
            {itineraries.length > 0 ? (
              itineraries.map((trip, index) => (
                <div key={index} className="itinerary-card">
                  <p><strong>From:</strong> {trip.startingPoint}</p>
                  <p><strong>To:</strong> {trip.destination}</p>
                  <p><strong>Date:</strong> {new Date(trip.startDate).toLocaleDateString()}</p>
                </div>
              ))
            ) : (
              <p>No itineraries available.</p>
            )}
          </div>
        </div>

        {/* Challenges Section */}
        <div className="profile-section">
          <h3>Challenges Completed</h3>
          <ul>
            {challenges.length > 0 ? (
              Object.entries(challenges).map(([key, value], index) => (
                <li key={index}>
                  {key} - <strong>{value ? "Completed" : "Not Completed"}</strong>
                </li>
              ))
            ) : (
              <p>No challenges completed.</p>
            )}
          </ul>
        </div>

        {/* Vouchers Section */}
        <div className="profile-section">
          <h3>Vouchers</h3>
          <div className="vouchers">
            {vouchers.length > 0 ? (
              vouchers.map((voucher, index) => (
                <div key={index} className="voucher-card">
                  <p><strong>{voucher.name}</strong></p>
                  <p>Required Points: {voucher.requiredPoints}</p>
                  {!voucher.isRedeemed ? (
                    <button onClick={() => handleRedeem(index)}>Redeem</button>
                  ) : (
                    <div className="redeemed-voucher">
                      🎊 Code: <strong>{Math.random().toString(36).substr(2, 8).toUpperCase()}</strong> 🎊
                    </div>
                  )}
                </div>
              ))
            ) : (
              <p>No vouchers available.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
