import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import axios from 'axios';
import './CheckoutPage.css';

const CheckoutPage = ({ totalBalance = 10000 }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    
    if (!stripe || !elements) {
      return;
    }

    try {
      const { data: { clientSecret } } = await axios.post('http://localhost:5000/create-payment-intent', {
        amount: totalBalance * 100 // Convert to cents
      });

      const { error: stripeError } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: elements.getElement(CardElement),
        }
      });

      if (stripeError) {
        setError(stripeError.message);
        setSuccess(false);
      } else {
        setSuccess(true);
        setError(null);
      }
    } catch (err) {
      setError(err.message);
      setSuccess(false);
    }
    setLoading(false);
  };

  return (
    <div className="checkout-container">
      <h2 className="checkout-heading">AI Trip Planner Payment</h2>
      <p className="total-amount">Total: PKR {totalBalance}</p>
      
      <form onSubmit={handleSubmit} className="checkout-form">
        <div className="card-element-container">
          <CardElement className="card-element" />
        </div>
        
        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">Payment Successful!</div>}
        
        <button 
          className="pay-button" 
          disabled={!stripe || loading}
        >
          {loading ? 'Processing...' : `Pay PKR ${totalBalance}`}
        </button>
      </form>
    </div>
  );
};

export default CheckoutPage;
