import React from 'react';

const PopularDestinations = () => {
  const destinations = [
    {
      id: 1,
      image: '/api/placeholder/400/320',
      country: 'Italy',
      name: 'San Miguel',
      description: 'Fusce hic augue velit wisi ips quibusdam pariatur, iusto.',
    },
    {
      id: 2,
      image: '/api/placeholder/400/320',
      country: 'Dubai',
      name: 'Burj Khalifa',
      description: 'Fusce hic augue velit wisi ips quibusdam pariatur, iusto.',
    },
    {
      id: 3,
      image: '/api/placeholder/400/320',
      country: 'Japan',
      name: 'Kyoto Temple',
      description: 'Fusce hic augue velit wisi ips quibusdam pariatur, iusto.',
    },
  ];

  return (
    <section className="py-16 bg-gray-100">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-2">Popular Destination</h2>
        <p className="text-center text-gray-600 mb-8">
          Fusce hic augue velit wisi quibusdam pariatur, iusto primis, nec nemo, rutrum. Vestibulum cumque laudantium. Sit ornare mollitia tenetur, aptent.
        </p>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {destinations.map((dest) => (
            <div key={dest.id} className="bg-white rounded-3xl overflow-hidden shadow-lg">
              <div className="relative h-64">
                <img src={dest.image} alt={dest.name} className="w-full h-full object-cover" />
                <div className="absolute top-4 right-4 bg-blue-600 text-white px-3 py-1 rounded-full text-sm flex items-center">
                  {[...Array(5)].map((_, i) => (
                    <svg key={i} xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" viewBox="0 0 20 20" fill="currentColor">
                      <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                    </svg>
                  ))}
                </div>
              </div>
              <div className="p-6">
                <p className="text-blue-600 text-sm font-semibold mb-2">{dest.country}</p>
                <h3 className="text-xl font-bold mb-2">{dest.name}</h3>
                <p className="text-gray-600">{dest.description}</p>
              </div>
            </div>
          ))}
        </div>
        <div className="text-center mt-10">
          <button className="bg-blue-600 text-white px-6 py-3 rounded-full font-semibold hover:bg-blue-700 transition duration-300">
            More Destination
          </button>
        </div>
      </div>
    </section>
  );
};

export default PopularDestinations;
