import React, { useState, useEffect } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './PaymentPage.css';

const PaymentPage = ({ tripData = {
  days: [
    { title: "Plan + Flight + Hotel", cost: 140000 },
    { title: "Plan + Bus + Hotel", cost: 25000 },
    { title: "Plan + Car + Hotel", cost: 32500 }
  ],
  discountPercentage: 10
} }) => {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [promoCode, setPromoCode] = useState('');

  // Calculate totals
  const subtotal = tripData.days.reduce((sum, day) => sum + day.cost, 0);
  const discount = (subtotal * tripData.discountPercentage) / 100;
  const total = subtotal - discount;

  useEffect(() => {
    if (success) {
      const timer = setTimeout(() => {
        navigate('/');
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [success, navigate]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    
    if (!stripe || !elements) return;

    try {
      const { data: { clientSecret } } = await axios.post('http://localhost:5000/create-payment-intent', {
        amount: total
      });

      const { error: stripeError } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: elements.getElement(CardElement),
        }
      });

      if (stripeError) {
        setError(stripeError.message);
        setSuccess(false);
      } else {
        setSuccess(true);
        setError(null);
      }
    } catch (err) {
      setError(err.message);
      setSuccess(false);
    }
    setLoading(false);
  };

  return (
    <div className="payment-container">
      <div className="payment-header">
        <h1>Payment</h1>
      </div>

      <div className="payment-content">
        <div className="card-details">
          <h2>Card Details:</h2>
          <div className="form-grid">
            <div className="form-group">
              <label>Card Number</label>
              <input type="text" placeholder="XXXX XXXX XXXX XXXX" disabled />
            </div>
            <div className="form-group">
              <label>Name on Card</label>
              <input type="text" placeholder="MILO KHAN" disabled />
            </div>
            <div className="form-group">
              <label>Valid Through</label>
              <input type="text" placeholder="MM/YYYY" disabled />
            </div>
            <div className="form-group">
              <label>Security Code</label>
              <input type="text" placeholder="XXX" disabled />
            </div>
          </div>
          
          <div className="stripe-element-container">
            <CardElement 
              options={{
                style: {
                  base: {
                    fontSize: '16px',
                    color: '#424770',
                    '::placeholder': {
                      color: '#aab7c4',
                    },
                  },
                  invalid: {
                    color: '#9e2146',
                  },
                },
              }}
            />
          </div>

          <div className="promo-code">
            <div className="form-group">
              <label>Redeem Points:</label>
              <div className="promo-input">
                <input 
                  type="text" 
                  value={promoCode}
                  onChange={(e) => setPromoCode(e.target.value)}
                  placeholder="Enter Code Here..."
                />
                <button className="apply-btn">APPLY</button>
              </div>
            </div>
          </div>
        </div>

        <div className="plan-details">
          <h2>Plan Details:</h2>
          <div className="cost-breakdown">
            {tripData.days.map((day, index) => (
              <div key={index} className="cost-row">
                <span>Day {index + 1}: {day.title}</span>
                <span>{day.cost.toLocaleString()}/-</span>
              </div>
            ))}
            <div className="subtotal cost-row">
              <span>Sub-total:</span>
              <span>{subtotal.toLocaleString()}/-</span>
            </div>
            <div className="discount cost-row">
              <span>Discount after Redeeming Points:</span>
              <span>{tripData.discountPercentage}%</span>
            </div>
            <div className="total cost-row">
              <span>TOTAL COST:</span>
              <span>{total.toLocaleString()}/-</span>
            </div>
          </div>

          {error && <div className="error-message">{error}</div>}
          {success && <div className="success-message">Payment successful!</div>}

          <button 
            className="confirm-payment-btn" 
            onClick={handleSubmit}
            disabled={!stripe || loading}
          >
            {loading ? 'Processing...' : 'CONFIRM PAYMENT'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default PaymentPage;
