import React, { useState, useEffect } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import { CreditCard } from 'lucide-react';
import './CheckoutPage.css';
import Navbar from '../NavBar/Navbar';

const Checkout2 = () => {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const location = useLocation();
  
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [promoCode, setPromoCode] = useState('');
  const [promoDiscount, setPromoDiscount] = useState(0);
  const [promoError, setPromoError] = useState(null);
  const [hotelData, setHotelData] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    if (location.state?.hotelData && location.state?.totalPrice) {
      setHotelData(location.state.hotelData);
      setTotalPrice(location.state.totalPrice);
    } else {
      setError("Missing trip details.");
      navigate('/tripdetails'); // Redirect back if no data is found
    }
  }, [location.state, navigate]);

  const calculateTotals = () => {
    const discount = (totalPrice * promoDiscount) / 100;
    const finalTotal = totalPrice - discount;
    return { subtotal: totalPrice, discount, total: finalTotal };
  };

  const handleApplyPromo = () => {
    const promoCodes = { 'SAVE10': 10, 'DISCOUNT20': 20 };
    
    if (promoCodes[promoCode.toUpperCase()]) {
      setPromoDiscount(promoCodes[promoCode.toUpperCase()]);
      setPromoError(null);
    } else {
      setPromoError("Invalid promo code.");
      setPromoDiscount(0);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    setError(null);

    if (!stripe || !elements) {
      setError("Stripe not initialized.");
      setLoading(false);
      return;
    }

    try {
      const { total } = calculateTotals();
      const response = await axios.post('http://localhost:5000/api/payment-intent', { amount: total });

      if (response.status !== 200) throw new Error(response.data.message);
      
      const { clientSecret } = response.data;
      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: { card: elements.getElement(CardElement) },
      });

      if (error) {
        setError(error.message);
        setSuccess(false);
      } else if (paymentIntent.status === 'succeeded') {
        setSuccess(true);
      }
    } catch (err) {
      setError("Payment error.");
    }

    setLoading(false);
  };

  if (!hotelData.length) {
    return <div>Loading trip details...</div>;
  }

  const { subtotal, discount, total } = calculateTotals();

  return (
    <div className="checkout-container">
      <Navbar />
      <div className="checkout-content">
        <h1>Checkout - Trip Details</h1>
        {hotelData.map((hotel, index) => (
          <div key={index} className="trip-info">
            <h2>{hotel.hotelName}</h2>
            <p>Price per night: ${hotel.hotelPrice}</p>
          </div>
        ))}
        <div className="promo-section">
          <input type="text" value={promoCode} onChange={(e) => setPromoCode(e.target.value)} placeholder="Enter promo code" />
          <button onClick={handleApplyPromo}>Apply</button>
          {promoError && <p className="error">{promoError}</p>}
        </div>
        <div className="cost-summary">
          <p>Subtotal: ${subtotal}</p>
          <p>Discount: -${discount}</p>
          <p>Total: ${total}</p>
        </div>
        <form onSubmit={handleSubmit}>
          <CardElement />
          <button type="submit" disabled={loading}>Pay ${total}</button>
          {error && <p className="error">{error}</p>}
        </form>
      </div>
    </div>
  );
};

export default Checkout2;
