import React from "react";
import { Link } from "react-router-dom";
import NavBar from "../NavBar/Navbar";
import hotelData from "../HotelsPage/clean_hotel_data.json";
import "./City.css";

const getUniqueCities = (data) => {
  const cities = new Set();
  data.forEach((hotel) => cities.add(hotel.city));
  return [...cities];
};

const cityImages = {
    Islamabad: "https://www.teflcourse.net/uploads/pakistan-featured-image.jpg",
    Lahore: "https://www.pakimag.com/files/2010/03/Lahore-Minar-e-Pakistan.jpg",
    Karachi: "https://cdn.britannica.com/85/128585-050-5A1BDD02/Karachi-Pakistan.jpg",
    Kashmir: "https://i.pinimg.com/originals/b6/90/40/b690404505b30195f0a643d8e52c81f4.jpg",
    Murree: "https://paktourismportal.com/wp-content/uploads/2021/12/Murree.jpg",
    Hunza: "https://upload.wikimedia.org/wikipedia/commons/3/35/Passu_Cones_in_Hunza.jpg",
    Skardu: "https://upload.wikimedia.org/wikipedia/commons/8/8b/Shangrila_Resort_Skardu.jpg",
    Chitral: "https://i.dawn.com/primary/2020/01/5e22e1bb87872.jpg",
    Gilgit: "https://upload.wikimedia.org/wikipedia/commons/a/a3/Fairy_Meadows_and_Nanga_Parbat.jpg",
    Multan: "https://live.staticflickr.com/5131/5457939358_c041af25f1_b.jpg",
    Abbottabad: "https://paktourismportal.com/wp-content/uploads/2021/12/Nathia-Gali-Abbottabad.jpg",
    Quetta: "https://upload.wikimedia.org/wikipedia/commons/2/22/Hanna_Lake%2C_Quetta.jpg",
    Naran: "https://www.destinations.com.pk/wp-content/uploads/2018/06/naran-lake-saiful-malook-1-1.jpg",
    Batakundi: "https://visitnaran.com/wp-content/uploads/2020/03/Batakundi-1200x900.jpg",
    Peshawar: "https://upload.wikimedia.org/wikipedia/commons/d/da/Qissa_Khwani_Bazaar_in_Peshawar.jpg",
    Faisalabad: "https://paktourismportal.com/wp-content/uploads/2021/12/Clock-Tower-Faisalabad.jpg",
  };
  

const City = () => {
  const cities = getUniqueCities(hotelData);

  return (
    <div className="city-container">
      <NavBar />
      <h2 className="city-title">Explore Cities</h2>
      <div className="city-grid">
        {cities.map((city, index) => (
          <Link to={`/city/${city.toLowerCase()}`} key={index} className="city-card">
            <img
              src={cityImages[city] || "https://via.placeholder.com/400"}
              alt={city}
              className="city-image"
            />
            <div className="city-info">
              <h3>{city}</h3>
              <p>Discover the best hotels in {city}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default City;
