import React, { useState, useEffect } from "react";
import Papa from "papaparse";
import hotelData from "./clean_hotel_data.json";
import "./Hotels.css";
import NavBar from "../NavBar/Navbar";

// List of local images to randomize
const hotelImages = [
  require("./hotel.jpg"),
  require("./hotel1.jpg"),
  require("./hotel2.jpg"),
  require("./hotel3.jpg"),
  require("./hotel4.jpg"),
  require("./hotel5.jpeg"),
  require("./hotel6.jpg"),
  require("./hotel7.jpg"),
  require("./hotel8.jpeg"),
  require("./hotel9.jpg"),
  require("./hotel10.jpg"),
  require("./hotel11.jpeg"),
  require("./hotel12.jpg"),
  require("./hotel13.jpg"),
  require("./hotel14.jpg"),
  require("./hotel15.jpeg"),
];

const getRandomImage = () => hotelImages[Math.floor(Math.random() * hotelImages.length)];

const getRandomRating = () => {
  const base = 3.5;
  const variation = Math.random() * Math.random() * 1.5;
  return (base + variation).toFixed(1);
};

const Hotels = () => {
  const [hotelsByCity, setHotelsByCity] = useState({});
  const [ratings, setRatings] = useState({});
  const [selectedHotel, setSelectedHotel] = useState(null);
  const [selectedHotelImage, setSelectedHotelImage] = useState(null);

  useEffect(() => {
    // Fetch and parse CSV file
    fetch("./ratings.csv")
      .then((response) => response.text())
      .then((csvText) => {
        Papa.parse(csvText, {
          header: true,
          skipEmptyLines: true,
          complete: (result) => {
            const ratingsMap = {};
            result.data.forEach(({ name, rating, category }) => {
              ratingsMap[name] = { rating: parseFloat(rating), category };
            });
            setRatings(ratingsMap);
          },
        });
      });
  }, []);

  useEffect(() => {
    if (Object.keys(ratings).length === 0) return;

    const enrichedHotels = hotelData.map((hotel) => ({
      ...hotel,
      rating: ratings[hotel.name]?.rating || getRandomRating(),
      category: ratings[hotel.name]?.category || hotel.category,
    }));

    const groupedHotels = enrichedHotels.reduce((acc, hotel) => {
      const { city } = hotel;
      if (!acc[city]) acc[city] = [];
      acc[city].push(hotel);
      return acc;
    }, {});

    // Sort by rating (highest first) and keep only top 10
    Object.keys(groupedHotels).forEach((city) => {
      groupedHotels[city] = groupedHotels[city]
        .sort((a, b) => b.rating - a.rating)
        .slice(0, 10);
    });

    setHotelsByCity(groupedHotels);
  }, [ratings]);

  const scroll = (id, direction) => {
    const container = document.getElementById(id);
    if (container) {
      container.scrollBy({ left: direction === "left" ? -300 : 300, behavior: "smooth" });
    }
  };

  const openPopup = (hotel) => {
    setSelectedHotel(hotel);
    setSelectedHotelImage(getRandomImage());
  };

  const closePopup = () => {
    setSelectedHotel(null);
  };

  return (
    <div className="hotels-container">
      <NavBar />
      {Object.entries(hotelsByCity).map(([city, hotels]) => (
        <div key={city} className="city-section">
          <h2 className="city-title">Hotels in {city}</h2>
          <div className="scroll-container">
            <button className="scroll-btn left" onClick={() => scroll(city, "left")}>‚Üê</button>
            <div className="hotels-row" id={city}>
              {hotels.map((hotel) => (
                <div key={hotel.id} className="hotel-card" onClick={() => openPopup(hotel)}>
                  <img src={getRandomImage()} alt={hotel.name} className="hotel-image" />
                  <h3>{hotel.name}</h3>
                  <p>‚≠ê {hotel.rating} ({hotel.category})</p>
                </div>
              ))}
            </div>
            <button className="scroll-btn right" onClick={() => scroll(city, "right")}>‚Üí</button>
          </div>
        </div>
      ))}

      {/* Pop-up Modal */}
      {selectedHotel && (
        <div className="popup">
          <div className="popup-content">
            <button className="close-btn" onClick={closePopup}>‚úñ</button>
            <img src={selectedHotelImage} alt={selectedHotel.name} className="popup-image" />
            <h2>{selectedHotel.name}</h2>
            <p><strong>üìç Address:</strong> {selectedHotel.address}</p>
            <p><strong>üí∞ Price:</strong> {selectedHotel.price} per night</p>
            <p><strong>üè® Facilities:</strong> {selectedHotel.facilities?.split(",").map((facility, index) => (
              <span key={index}> {getFacilityEmoji(facility.trim())} {facility.trim()} | </span>
            ))}</p>
          </div>
        </div>
      )}
    </div>
  );
};

// Function to assign fun icons to hotel facilities
const getFacilityEmoji = (facility) => {
  const emojis = {
    "Free WiFi": "üì∂",
    "Swimming Pool": "üèä",
    "Gym": "üèãÔ∏è",
    "Restaurant": "üçΩÔ∏è",
    "Spa": "üíÜ",
    "Parking": "üÖøÔ∏è",
    "Air Conditioning": "‚ùÑÔ∏è",
    "Bar": "üç∏",
    "24-hour Front Desk": "üïõ",
  };
  return emojis[facility] || "‚úÖ"; // Default emoji
};

export default Hotels;
