import React, { useState, useEffect } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import { CreditCard, Lock, Calendar, User, Shield } from 'lucide-react';
import './CheckoutPage.css';
import Navbar from '../NavBar/Navbar';

const Checkout2 = () => {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const location = useLocation();
  
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [promoCode, setPromoCode] = useState('');
  const [tripData, setTripData] = useState(null);
  const [initialLoad, setInitialLoad] = useState(true);
  const [promoDiscount, setPromoDiscount] = useState(0);
  const [promoError, setPromoError] = useState(null);

  useEffect(() => {
    const fetchTripData = async () => {
      try {
        if (location.state?.tripId) {
          const response = await axios.get(`http://localhost:5000/api/tripdetails/${location.state.tripId}`);
          if (response.data) {
            setTripData(response.data);
          } else {
            setError("Trip details not found.");
          }
        } else {
          navigate('/tripDetails');
        }
      } catch (err) {
        setError("Could not load trip details.");
      } finally {
        setInitialLoad(false);
      }
    };

    fetchTripData();
  }, [location.state, navigate]);

  const calculateTotals = () => {
    if (!tripData) return { subtotal: 0, discount: 0, total: 0 };
    
    const subtotal = tripData.cost || 0;
    const discount = (subtotal * promoDiscount) / 100;
    const total = subtotal - discount;
    
    return { subtotal, discount, total };
  };

  const handleApplyPromo = () => {
    const promoCodes = { 'SAVE10': 10, 'DISCOUNT20': 20 };
    
    if (promoCodes[promoCode.toUpperCase()]) {
      setPromoDiscount(promoCodes[promoCode.toUpperCase()]);
      setPromoError(null);
    } else {
      setPromoError("Invalid promo code.");
      setPromoDiscount(0);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    setError(null);

    if (!stripe || !elements) {
      setError("Stripe not initialized.");
      setLoading(false);
      return;
    }

    try {
      const { total } = calculateTotals();
      const response = await axios.post('http://localhost:5000/api/payment-intent', { amount: total });

      if (response.status !== 200) throw new Error(response.data.message);
      
      const { clientSecret } = response.data;
      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: { card: elements.getElement(CardElement) },
      });

      if (error) {
        setError(error.message);
        setSuccess(false);
      } else if (paymentIntent.status === 'succeeded') {
        setSuccess(true);
        await axios.patch(`http://localhost:5000/api/tripdetails/${location.state.tripId}`, { status: 'paid' });
      }
    } catch (err) {
      setError("Payment error.");
    }

    setLoading(false);
  };

  if (initialLoad) {
    return <div>Loading...</div>;
  }

  if (!tripData) {
    return <div>Error loading trip details.</div>;
  }

  const { subtotal, discount, total } = calculateTotals();

  return (
    <div className="checkout-container">
      <Navbar />
      <div className="checkout-content">
        <h1>Checkout - Trip Details</h1>
        <div className="trip-info">
          <h2>{tripData.destination}</h2>
          <p>Duration: {tripData.duration} days</p>
          <p>Cost: ${subtotal}</p>
        </div>
        <div className="promo-section">
          <input type="text" value={promoCode} onChange={(e) => setPromoCode(e.target.value)} placeholder="Enter promo code" />
          <button onClick={handleApplyPromo}>Apply</button>
          {promoError && <p className="error">{promoError}</p>}
        </div>
        <div className="cost-summary">
          <p>Subtotal: ${subtotal}</p>
          <p>Discount: -${discount}</p>
          <p>Total: ${total}</p>
        </div>
        <form onSubmit={handleSubmit}>
          <CardElement />
          <button type="submit" disabled={loading}>Pay ${total}</button>
          {error && <p className="error">{error}</p>}
        </form>
      </div>
    </div>
  );
};

export default Checkout2;
