import React, { useState } from 'react';
import './ToursSection.css';

const ToursSection = () => {
  const [activeIndex, setActiveIndex] = useState(2);

  const tours = [
    {
      id: 1,
      title: "Park Guell Guided Tour",
      image: "https://gulmitcontinentalhotel.com/wp-content/uploads/2022/10/Autum-season-in-Hunza-Valley.jpg",
      category: "Historic",
      description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
      price: 54,
      duration: "1h 30min",
      rating: 95
    },
    // Add more tour objects here...
  ];

  const handlePrev = () => {
    setActiveIndex((prevIndex) => (prevIndex === 0 ? tours.length - 1 : prevIndex - 1));
  };

  const handleNext = () => {
    setActiveIndex((prevIndex) => (prevIndex === tours.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <section className="tours_section">
      <div className="container">
        <div className="main_title">
          <span><em></em></span>
          <h2>Our Popular Tours</h2>
          <p>Cum doctus civibus efficiantur in imperdiet deterruisset.</p>
        </div>
        <div className="tours_carousel">
          {tours.map((tour, index) => (
            <div
              key={tour.id}
              className={`tour_item ${index === activeIndex ? 'active' : ''}`}
              style={{
                transform: `translateX(${(index - activeIndex) * 100}%)`,
                opacity: index === activeIndex ? 1 : 0.5,
                zIndex: index === activeIndex ? 1 : 0
              }}
            >
              <div className="box_grid">
                <figure>
                  <img src={tour.image} alt={tour.title} />
                  <div className="read_more"><span>Read more</span></div>
                  <small>{tour.category}</small>
                </figure>
                <div className="wrapper">
                  <h3>{tour.title}</h3>
                  <p>{tour.description}</p>
                  <span className="price">From <strong>${tour.price}</strong> /per person</span>
                </div>
                <ul>
                  <li><i className="icon_clock_alt"></i> {tour.duration}</li>
                  <li><i className="icon_like"></i> {tour.rating}%</li>
                  <li><a href="/tour-detail">Book Now</a></li>
                </ul>
              </div>
            </div>
          ))}
        </div>
        <div className="carousel_controls">
          <button onClick={handlePrev}>&lt;</button>
          <button onClick={handleNext}>&gt;</button>
        </div>
      </div>
    </section>
  );
};

export default ToursSection;
