import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Navbar from '../../NavBar/Navbar';
import './UserCustomization.css';

const UserCustomization = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { customizationData, bookingData } = location.state || {};

  const {
    startingPoint = '',
    destination = '',
    startDate = '',
    endDate = '',
    guests = 1
  } = customizationData || {};

  const [selectedStartingPoint] = useState(startingPoint);
  const [selectedDestination] = useState(destination);
  const [selectedStartDate] = useState(startDate);
  const [selectedEndDate] = useState(endDate);
  const [selectedGuests] = useState(guests);
  const [numberOfDays, setNumberOfDays] = useState(0);

  // State to store booked hotel, transport, food and attractions details for each day
  const [dayDetails, setDayDetails] = useState({});

  // Define food and attraction options for different cities
  const cityOptions = {
    "Islamabad": {
      lunch: ["Monal Restaurant", "Tandoori Restaurant", "Chaaye Khana"],
      dinner: ["Savour Foods", "Kabul Restaurant", "Dynasty Restaurant"],
      attractions: ["Faisal Mosque", "Daman-e-Koh", "Pakistan Monument"]
    },
    "Lahore": {
      lunch: ["Haveli Restaurant", "Andaaz Restaurant", "Spice Bazaar"],
      dinner: ["Cooco's Den", "Village Restaurant", "Fujiyama Restaurant"],
      attractions: ["Badshahi Mosque", "Lahore Fort", "Shalimar Gardens"]
    },
    "Karachi": {
      lunch: ["BBQ Tonight", "Kolachi Restaurant", "Kababjees"],
      dinner: ["Port Grand", "Okra Restaurant", "Cafe Flo"],
      attractions: ["Clifton Beach", "Mazar-e-Quaid", "Mohatta Palace"]
    },
    "Murree": {
      lunch: ["Gloria Jeans Coffee", "Usmania Restaurant", "Whispering Pines"],
      dinner: ["Patio Restaurant", "Saffron Restaurant", "Red Onion"],
      attractions: ["Mall Road", "Patriata Chair Lift", "Kashmir Point"]
    },
    "Peshawar": {
      lunch: ["Charsi Tikka", "Chief Restaurant", "Cafe Peshawar"],
      dinner: ["Namak Mandi", "Ghiza Restaurant", "Yasir Broast"],
      attractions: ["Peshawar Museum", "Qissa Khwani Bazaar", "Bala Hisar Fort"]
    },
    "Skardu": {
      lunch: ["Shangri-La Resort", "K2 Restaurant", "Mountain View Cafe"],
      dinner: ["Serena Hotel", "Snowland Restaurant", "Khaplu Palace"],
      attractions: ["Shangrila Resort", "Upper Kachura Lake", "Deosai National Park"]
    },
    "Hunza": {
      lunch: ["Eagles Nest Restaurant", "Cafe de Hunza", "Hidden Paradise"],
      dinner: ["Hunza Serena Inn", "Karimabad View", "Mountain Retreat"],
      attractions: ["Baltit Fort", "Attabad Lake", "Eagle's Nest Viewpoint"]
    },
    "Quetta": {
      lunch: ["Cafe China", "Lal Kabab", "Quetta Restaurant"],
      dinner: ["Habib Restaurant", "Usmania Restaurant", "Lehri Restaurant"],
      attractions: ["Hanna Lake", "Quetta Archaeological Museum", "Hazarganji Chiltan National Park"]
    },
    "Multan": {
      lunch: ["Bundu Khan", "Multan Food Court", "Shalimar Restaurant"],
      dinner: ["Bahauddin", "Multan Palace", "Pearl Continental Hotel"],
      attractions: ["Tomb of Shah Rukn-e-Alam", "Multan Fort", "Ghanta Ghar"]
    },
    "Faisalabad": {
      lunch: ["Lasania Restaurant", "Andaaz Restaurant", "Al-Masoom Hotel"],
      dinner: ["Chenab Club", "Royalton Hotel", "Gourmet Restaurant"],
      attractions: ["Clock Tower", "Jinnah Garden", "Lyallpur Museum"]
    },
    "Kashmir": {
      lunch: ["Pearl Continental", "Neelum View Hotel", "Kashmir Point"],
      dinner: ["Alpine Hotel", "Royal Restaurant", "Muzaffarabad View"],
      attractions: ["Neelum Valley", "Pir Chinasi", "Keran"]
    },
    "Abbottabad": {
      lunch: ["Usmania Restaurant", "Taj Mahal Hotel", "Mona Lisa Cafe"],
      dinner: ["Saigol Restaurant", "Abbottabad Serena Hotel", "Green Cottage"],
      attractions: ["Ilyasi Mosque", "Shimla Hill", "Jinnah Garden"]
    },
    "Gilgit": {
      lunch: ["PTDC Motel", "Mehran Hotel", "Mountain Bistro"],
      dinner: ["Gilgit Serena", "Chinar Inn", "Destination Restaurant"],
      attractions: ["Naltar Valley", "Kargah Buddha", "Jutial Valley"]
    },
    "Naran": {
      lunch: ["PTDC Motel Naran", "Saif-ul-Malook View", "River View Restaurant"],
      dinner: ["Hotel Green Park", "Pine Park", "Miandam Restaurant"],
      attractions: ["Lake Saif-ul-Malook", "Lulusar Lake", "Babusar Top"]
    },
    "Batakundi": {
      lunch: ["Batakundi Resort", "Lalazar View", "Mountain Dine"],
      dinner: ["Fairy Point Hotel", "Pine View Restaurant", "Valley Resort"],
      attractions: ["Lalazar", "Batakundi Meadows", "Lulusar Lake"]
    }
  };

  // Default options for any city not in the list
  const defaultOptions = {
    lunch: ["Local Restaurant 1", "Local Restaurant 2", "Local Restaurant 3"],
    dinner: ["Evening Dining 1", "Evening Dining 2", "Evening Dining 3"],
    attractions: ["Local Attraction 1", "Local Attraction 2", "Local Attraction 3"]
  };

  const citiesInPakistan = [
    "Islamabad", "Lahore", "Karachi", "Murree", "Peshawar",
    "Skardu", "Hunza", "Quetta", "Multan", "Faisalabad", 
    "Murree", "Kashmir", "Abbottabad", "Gilgit", "Naran",
    "Batakundi"
  ];

  // Calculate number of days for the trip
  useEffect(() => {
    if (selectedStartDate && selectedEndDate) {
      const start = new Date(selectedStartDate);
      const end = new Date(selectedEndDate);
      const timeDiff = end - start;
      const daysDiff = Math.ceil(timeDiff / (1000 * 60 * 60 * 24)) + 1;
      setNumberOfDays(daysDiff > 0 ? daysDiff : 0);
    }
  }, [selectedStartDate, selectedEndDate]);

  // Load day details from localStorage on component mount
  useEffect(() => {
    const savedDetails = localStorage.getItem('dayDetails');
    if (savedDetails) {
      try {
        setDayDetails(JSON.parse(savedDetails));
      } catch (error) {
        console.error("Error parsing day details from localStorage:", error);
        localStorage.removeItem('dayDetails');
      }
    }
  }, []);

  // Update localStorage whenever dayDetails state changes
  useEffect(() => {
    if (Object.keys(dayDetails).length > 0) {
      localStorage.setItem('dayDetails', JSON.stringify(dayDetails));
    }
  }, [dayDetails]);

  // Receive booking data and update state
  useEffect(() => {
    if (bookingData) {
      console.log("Received Booking Data:", bookingData);
      const { dayIndex, selectedHotel, selectedTransport } = bookingData;
      
      setDayDetails(prevDetails => {
        const updatedDetails = {
          ...prevDetails,
          [dayIndex]: {
            ...prevDetails[dayIndex],
            hotel: selectedHotel,
            transport: selectedTransport
          }
        };
        
        // Save to localStorage immediately to prevent data loss
        localStorage.setItem('dayDetails', JSON.stringify(updatedDetails));
        return updatedDetails;
      });
    }
  }, [bookingData]);

  // Navigate to BookOption page
  const handleEditClick = (day) => {
    navigate('/bookoption', { 
      state: { 
        day, 
        customizationData,
        currentDetails: dayDetails[day] || {} 
      } 
    });
  };

  // Handle change for lunch, dinner, and attractions
  const handleDetailChange = (day, type, value) => {
    setDayDetails(prevDetails => {
      const updatedDetails = {
        ...prevDetails,
        [day]: {
          ...prevDetails[day],
          [type]: value
        }
      };
      
      // Save to localStorage
      localStorage.setItem('dayDetails', JSON.stringify(updatedDetails));
      return updatedDetails;
    });
  };

  // Helper function to safely access nested properties
  const getPropertyValue = (obj, property, fallback = 'N/A') => {
    if (!obj) return fallback;
    
    const properties = property.split('.');
    let value = obj;
    
    for (const prop of properties) {
      if (value && typeof value === 'object' && prop in value) {
        value = value[prop];
      } else {
        return fallback;
      }
    }
    
    return value || fallback;
  };

  // Format date to be more readable
  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      weekday: 'short', 
      month: 'short', 
      day: 'numeric', 
      year: 'numeric' 
    });
  };

  // Get options for the current destination
  const getOptionsForDestination = () => {
    return cityOptions[selectedDestination] || defaultOptions;
  };

  // Calculate total cost including food and attractions
  const calculateTotalCost = () => {
    let totalCost = 0;
    let hotelCost = 0;
    let transportCost = 0;
    let foodCost = 0;
    let attractionsCost = 0;
    
    // Assumed costs for food and attractions
    const LUNCH_COST = 1000; // PKR
    const DINNER_COST = 1500; // PKR
    const ATTRACTION_COST = 500; // PKR per person
    
    Object.values(dayDetails).forEach(day => {
      if (day.hotel && day.hotel.price) {
        const price = parseFloat(day.hotel.price);
        if (!isNaN(price)) {
          hotelCost += price;
          totalCost += price;
        }
      }
      
      if (day.transport && day.transport.price) {
        const price = parseFloat(day.transport.price);
        if (!isNaN(price)) {
          transportCost += price;
          totalCost += price;
        }
      }
      
      // Add lunch cost if selected
      if (day.lunch) {
        foodCost += LUNCH_COST;
        totalCost += LUNCH_COST;
      }
      
      // Add dinner cost if selected
      if (day.dinner) {
        foodCost += DINNER_COST;
        totalCost += DINNER_COST;
      }
      
      // Add attractions cost if selected (per person)
      if (day.attraction) {
        const attractionFee = ATTRACTION_COST * selectedGuests;
        attractionsCost += attractionFee;
        totalCost += attractionFee;
      }
    });
    
    return { totalCost, hotelCost, transportCost, foodCost, attractionsCost };
  };

  return (
    <div className="customization-container">
      <Navbar />
      <h2 className="customization-title">Your Trip Details</h2>
      
      <div className="trip-summary">
        <h3>Trip Summary</h3>
        <div className="summary-details">
          <p><strong>From:</strong> {selectedStartingPoint}</p>
          <p><strong>To:</strong> {selectedDestination}</p>
          <p><strong>Start Date:</strong> {formatDate(selectedStartDate)}</p>
          <p><strong>End Date:</strong> {formatDate(selectedEndDate)}</p>
          <p><strong>Guests:</strong> {selectedGuests} {selectedGuests === 1 ? 'Person' : 'Persons'}</p>
          <p><strong>Duration:</strong> {numberOfDays} {numberOfDays === 1 ? 'Day' : 'Days'}</p>
        </div>
      </div>
      
      {/* Display Day Cards */}
      {numberOfDays > 0 && (
        <div className="days-section">
          <h3>Day-by-Day Itinerary</h3>
          {[...Array(numberOfDays)].map((_, index) => {
            const dayNumber = index + 1;
            const details = dayDetails[dayNumber] || {};
            const options = getOptionsForDestination();
            
            // Calculate day date
            const dayDate = selectedStartDate ? new Date(selectedStartDate) : new Date();
            dayDate.setDate(dayDate.getDate() + index);
            
            return (
              <div key={index} className="day-card">
                <div className="day-header">
                  <div className="day-number">DAY {dayNumber}</div>
                  <div className="day-date">{formatDate(dayDate)}</div>
                  <button 
                    className="edit-button"
                    onClick={() => handleEditClick(dayNumber)}
                  >
                    <i className="fas fa-edit"></i> Edit
                  </button>
                </div>
                
                <div className="day-line"></div>
                
                <div className="booking-details-container">
                  {/* Hotel and Transport Details */}
                  {details.hotel ? (
                    <div className="booking-details hotel-details">
                      <h4>Hotel</h4>
                      <p><strong>Name:</strong> {getPropertyValue(details.hotel, 'name')}</p>
                      <p><strong>Price:</strong> {getPropertyValue(details.hotel, 'price')} PKR</p>
                      <p><strong>Rating:</strong> {getPropertyValue(details.hotel, 'rating')}/5</p>
                      {details.hotel.address && (
                        <p><strong>Address:</strong> {details.hotel.address}</p>
                      )}
                    </div>
                  ) : (
                    <div className="no-booking-info">
                      <p>No hotel selected</p>
                      <button 
                        className="small-add-button"
                        onClick={() => handleEditClick(dayNumber)}
                      >
                        Add Hotel
                      </button>
                    </div>
                  )}
                  
                  {details.transport ? (
                    <div className="booking-details transport-details">
                      <h4>Transport</h4>
                      <p><strong>Type:</strong> {getPropertyValue(details.transport, 'type', 'Vehicle')}</p>
                      <p>
                        <strong>Name:</strong> {
                          getPropertyValue(details.transport, 'name') || 
                          getPropertyValue(details.transport, 'car_name') || 
                          getPropertyValue(details.transport, 'bus_name') || 
                          getPropertyValue(details.transport, 'train_name')
                        }
                      </p>
                      <p><strong>Price:</strong> {getPropertyValue(details.transport, 'price')} PKR</p>
                      <p><strong>Departure:</strong> {getPropertyValue(details.transport, 'departure_time')}</p>
                      {details.transport.arrival_time && (
                        <p><strong>Arrival:</strong> {details.transport.arrival_time}</p>
                      )}
                    </div>
                  ) : (
                    <div className="no-booking-info">
                      <p>No transport selected</p>
                      <button 
                        className="small-add-button"
                        onClick={() => handleEditClick(dayNumber)}
                      >
                        Add Transport
                      </button>
                    </div>
                  )}
                  
                  {/* New Food and Attractions Section */}
                  <div className="food-attractions-section">
                    <h4>Dining & Attractions</h4>
                    
                    <div className="select-container">
                      <label htmlFor={`lunch-day-${dayNumber}`}>Lunch:</label>
                      <select
                        id={`lunch-day-${dayNumber}`}
                        value={details.lunch || ''}
                        onChange={(e) => handleDetailChange(dayNumber, 'lunch', e.target.value)}
                        className="custom-select"
                      >
                        <option value="">Select Lunch Place</option>
                        {options.lunch.map((place, i) => (
                          <option key={i} value={place}>{place}</option>
                        ))}
                      </select>
                    </div>
                    
                    <div className="select-container">
                      <label htmlFor={`dinner-day-${dayNumber}`}>Dinner:</label>
                      <select
                        id={`dinner-day-${dayNumber}`}
                        value={details.dinner || ''}
                        onChange={(e) => handleDetailChange(dayNumber, 'dinner', e.target.value)}
                        className="custom-select"
                      >
                        <option value="">Select Dinner Place</option>
                        {options.dinner.map((place, i) => (
                          <option key={i} value={place}>{place}</option>
                        ))}
                      </select>
                    </div>
                    
                    <div className="select-container">
                      <label htmlFor={`attraction-day-${dayNumber}`}>Attraction:</label>
                      <select
                        id={`attraction-day-${dayNumber}`}
                        value={details.attraction || ''}
                        onChange={(e) => handleDetailChange(dayNumber, 'attraction', e.target.value)}
                        className="custom-select"
                      >
                        <option value="">Select Attraction</option>
                        {options.attractions.map((place, i) => (
                          <option key={i} value={place}>{place}</option>
                        ))}
                      </select>
                    </div>
                    
                    {/* Display selected food & attractions */}
                    <div className="selected-options">
                      {details.lunch && (
                        <div className="selected-item">
                          <span className="icon lunch-icon"><i className="fas fa-utensils"></i></span>
                          <span>Lunch: {details.lunch}</span>
                        </div>
                      )}
                      
                      {details.dinner && (
                        <div className="selected-item">
                          <span className="icon dinner-icon"><i className="fas fa-moon"></i></span>
                          <span>Dinner: {details.dinner}</span>
                        </div>
                      )}
                      
                      {details.attraction && (
                        <div className="selected-item">
                          <span className="icon attraction-icon"><i className="fas fa-map-marker-alt"></i></span>
                          <span>Attraction: {details.attraction}</span>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}
      
      {/* Calculate and display total trip cost */}
      {Object.keys(dayDetails).length > 0 && (
        <div className="trip-cost-summary">
          <h3>Total Trip Cost</h3>
          {(() => {
            const { totalCost, hotelCost, transportCost, foodCost, attractionsCost } = calculateTotalCost();
            
            return (
              <div className="cost-breakdown">
                <p><strong>Hotel Costs:</strong> {hotelCost.toLocaleString()} PKR</p>
                <p><strong>Transport Costs:</strong> {transportCost.toLocaleString()} PKR</p>
                <p><strong>Food Costs:</strong> {foodCost.toLocaleString()} PKR</p>
                <p><strong>Attractions Costs:</strong> {attractionsCost.toLocaleString()} PKR</p>
                <p className="total-cost"><strong>Total Cost:</strong> {totalCost.toLocaleString()} PKR</p>
              </div>
            );
          })()}
        </div>
      )}
      
      <div className="action-buttons">
        <button 
          className="back-button"
          onClick={() => navigate(-1)}
        >
          Back
        </button>
        <button 
          className="save-button"
          onClick={() => {
            // Save final itinerary logic would go here
            alert('Your itinerary has been saved!');
          }}
        >
          Save Itinerary
        </button>
      </div>
    </div>
  );
};

export default UserCustomization;
