import React from "react";
import { Link } from "react-router-dom";
import NavBar from "../NavBar/Navbar";
import hotelData from "../HotelsPage/clean_hotel_data.json";
import "./City.css";

const getUniqueCities = (data) => {
  const cities = new Set();
  data.forEach((hotel) => cities.add(hotel.city));
  return [...cities];
};

const cityImages = {
  Islamabad: "https://www.teflcourse.net/uploads/pakistan-featured-image.jpg",
  Lahore: "https://www.pakimag.com/files/2010/03/Lahore-Minar-e-Pakistan.jpg",
  Multan: "https://live.staticflickr.com/5131/5457939358_c041af25f1_b.jpg",
  Karachi: "https://cdn.britannica.com/85/128585-050-5A1BDD02/Karachi-Pakistan.jpg",
  Murree: "https://pakistantourntravel.com/wp-content/uploads/2018/07/murree-places.jpg",
  Skardu: "https://www.skardu.pk/wp-content/uploads/2020/01/shangrila.jpg",
  Hunza: "https://www.travelcrafters.com.au/wp-content/uploads/2021/05/Hunza-1024x704.jpg",
};

const City = () => {
  const cities = getUniqueCities(hotelData);

  return (
    <div className="city-container">
      <NavBar />
      <h2 className="city-title">Explore Cities</h2>
      <div className="city-grid">
        {cities.map((city, index) => (
          <Link to={`/city/${city.toLowerCase()}`} key={index} className="city-card">
            <img
              src={cityImages[city] || "https://via.placeholder.com/400"}
              alt={city}
              className="city-image"
            />
            <div className="city-info">
              <h3>{city}</h3>
              <p>Discover the best hotels in {city}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default City;
