import React, { useState, useEffect } from "react";
import Navbar from "../NavBar/Navbar";
import "./MiloChatbot.css";

const MiloChatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [welcomeVisible, setWelcomeVisible] = useState(true);
  const [history, setHistory] = useState([]);
  const [historyIndex, setHistoryIndex] = useState(-1);
  const [isTyping, setIsTyping] = useState(false);
  const [rightContainerVisible, setRightContainerVisible] = useState(false);
  const [itineraryData, setItineraryData] = useState([]);

  const apiBaseURL = "http://127.0.0.1:5001/";

  useEffect(() => {
    setMessages([
      { sender: "bot", text: "Hi, I'm Milo, your personal travel assistant! Let's start planning your trip." }
    ]);
  }, []);

  const handleUserMessage = async () => {
    if (!input.trim()) return;

    const userMessage = input.trim();
    setMessages((prev) => [...prev, { sender: "user", text: userMessage }]);
    setHistory([userMessage, ...history]);
    setInput("");
    setWelcomeVisible(false);
    setIsTyping(true);

    let endpoint = "/MiloChatbot";
    let payload = { user_input: userMessage };

    if (/generate itinerary|plan a trip|form a plan|create a trip/i.test(userMessage)) {
      const userInputParts = userMessage.split(" ");
      const startingCity = userInputParts.includes("from") ? userInputParts[userInputParts.indexOf("from") + 1] : "Islamabad";
      const destinationCity = userInputParts.includes("to") ? userInputParts[userInputParts.indexOf("to") + 1] : "Lahore";
      const daysMatch = userMessage.match(/(\d+)\s+days/);
      const days = daysMatch ? parseInt(daysMatch[1], 10) : 3;

      endpoint = "/generate-itinerary";
      payload = { user_input: userMessage, starting_city: startingCity, destination_city: destinationCity, days };

      setMessages((prev) => [...prev, { sender: "milo", text: "Generating your itinerary. Please wait..." }]);
    } 
    
    else if (/top|best/i.test(userMessage)) {
      const match = userMessage.match(/top\s+(\d+)?\s*(hotels|restaurants|attractions|airbnbs?)\s+in\s+([\w\s]+)/i);
      if (match) {
        const numRecommendations = parseInt(match[1], 10) || 5;
        const category = match[2].toLowerCase();
        const city = match[3].trim().toLowerCase();

        endpoint = "/retrieve-top-items";
        payload = { user_input: userMessage, num_recommendations: numRecommendations, category, city };

        setMessages((prev) => [...prev, { sender: "milo", text: "Fetching top recommendations..." }]);
      } else {
        setMessages((prev) => [...prev, { sender: "milo", text: "Please specify what type of top information you need (e.g., 'top 5 hotels in Lahore')." }]);
        setIsTyping(false);
        return;
      }
    } 
    
    else if (/tell me about|what is|describe|give details on|where is|give info on/i.test(userMessage)) {
      endpoint = "/retrieve-details";
    } 
    
    else if (/compare/i.test(userMessage)) {
      const match = userMessage.match(/compare\s+([\w\s]+)\s+with\s+([\w\s]+)/i);
      if (match) {
        const option1 = match[1].trim();
        const option2 = match[2].trim();
        endpoint = "/compare-two-options";
        payload = { option1, option2 };

        setMessages((prev) => [...prev, { sender: "milo", text: `Comparing ${option1} with ${option2}...` }]);
      } else {
        setMessages((prev) => [...prev, { sender: "milo", text: "Please specify two items to compare (e.g., 'compare Serena Hotel with Pearl Continental')." }]);
        setIsTyping(false);
        return;
      }
    }

    try {
      const response = await fetch(`${apiBaseURL}${endpoint}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      if (!response.ok) throw new Error("Server responded with an error");

      const data = await response.json();
      console.log("Backend response:", data);  // Debugging line

      let botResponse = "";

      if (endpoint === "/retrieve-details" && data.result) {
        botResponse = data.result.replace(/\n/g, "<br>");
      } 
      else if (endpoint === "/retrieve-top-items" && data.result) {
        botResponse = "Here are the top recommendations:";
        setRightContainerVisible(true);
        setItineraryData(data.result.split("\n").filter((line) => line.trim() !== ""));
      } 
      else if (endpoint === "/generate-itinerary" && data.response) {
        botResponse = "Here's your trip itinerary:";
        setRightContainerVisible(true);

        // Ensure data.response is properly formatted
        if (typeof data.response === "string") {
          setItineraryData(data.response.split("\n").filter((line) => line.trim() !== ""));
        } else if (Array.isArray(data.response)) {
          setItineraryData(data.response);
        } else {
          console.error("Unexpected itinerary format:", data.response);
          botResponse = "Sorry, something went wrong with the itinerary generation.";
        }
      } 
      else if (endpoint === "/compare-two-options" && data.result) {
        botResponse = data.result;
      }

      if (!botResponse.trim()) {
        botResponse = "Sorry, I couldn't process your request. Can you rephrase it?";
      }

      setMessages((prev) => [...prev, { sender: "milo", text: botResponse }]);
    } 
    catch (error) {
      console.error("Error communicating with backend:", error);
      setMessages((prev) => [...prev, { sender: "milo", text: "Sorry, something went wrong. Please try again later." }]);
    } 
    finally {
      setIsTyping(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleUserMessage();
    } 
    else if (e.key === "ArrowUp" && historyIndex < history.length - 1) {
      setInput(history[historyIndex + 1]);
      setHistoryIndex(historyIndex + 1);
    } 
    else if (e.key === "ArrowDown" && historyIndex > 0) {
      setInput(history[historyIndex - 1]);
      setHistoryIndex(historyIndex - 1);
    }
  };

  const closeRightContainer = () => {
    setRightContainerVisible(false);
    setTimeout(() => setItineraryData([]), 300);
  };

  return (
    <div className={`chatbot-container ${rightContainerVisible ? "shift-left" : ""}`}>
      <Navbar />
      <h3>Welcome to a chat with Milo, your personal smart AI Trip Planner!</h3>

      {welcomeVisible && <div className="welcome-message"><h4>Click on the input bar to start the chat.</h4></div>}

      <div className="chat-container">
        {messages.map((msg, index) => (
          <div key={index} className={`chat-bubble ${msg.sender}`}>
            <span>{msg.sender === "user" ? `You: ${msg.text}` : `Milo: ${msg.text}`}</span>
          </div>
        ))}
        {isTyping && <div className="chat-bubble milo"><span>Milo is typing...</span></div>}
      </div>

      {rightContainerVisible && (
        <div className="itinerary-container">
          <button className="close-itinerary" onClick={closeRightContainer}>&times;</button>
          <h2>Itinerary</h2>
          {itineraryData.map((line, index) => <p key={index} className="itinerary-text">{line}</p>)}
        </div>
      )}

      <div className="input-container">
        <input type="text" placeholder="Type your message here..." value={input} onChange={(e) => setInput(e.target.value)} onKeyDown={handleKeyDown} />
        <button onClick={handleUserMessage}>Send</button>
      </div>
    </div>
  );
};

export default MiloChatbot;
