import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaHotel, FaUtensils, FaSearch, FaSpinner } from 'react-icons/fa';
import './Navbar.css';
import axios from 'axios';

const Navbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState({ hotels: [], restaurants: [] });
  const [searchOpen, setSearchOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const checkAuthentication = () => {
      const token = localStorage.getItem('token');
      setIsAuthenticated(!!token);
    };

    checkAuthentication();
    window.addEventListener('storage', checkAuthentication);

    return () => {
      window.removeEventListener('storage', checkAuthentication);
    };
  }, []);

  const signOut = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsAuthenticated(false);
    navigate('/login');
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (searchQuery) {
      setIsLoading(true);
      setError(null);
      try {
        const { data } = await axios.get(`http://localhost:5000/api/search`, { params: { city: searchQuery } });
        setSearchResults(data);
      } catch (error) {
        console.error('Error fetching search results:', error);
        setError('An error occurred while searching. Please try again.');
      } finally {
        setIsLoading(false);
      }
    }
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light fixed-top">
      <div className="container-fluid">
        <Link to="/" className="navbar-brand">
          <img src="/path-to-your-logo.png" alt="Logo" className="logo" />
          <span>Voyaige</span>
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto">
            <li className="nav-item">
              <Link className="nav-link" to="/">Home</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/tours">Tours</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/hotels">Hotels</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/restaurants">Eat & Drink</Link>
            </li>
            {isAuthenticated ? (
              <li className="nav-item">
                <button className="btn btn-outline-danger ms-2" onClick={signOut}>Log Out</button>
              </li>
            ) : (
              <li className="nav-item">
                <Link className="btn btn-outline-primary ms-2" to="/login">Sign In</Link>
              </li>
            )}
            <li className="nav-item">
              <button onClick={() => setSearchOpen(!searchOpen)} className="btn btn-primary ms-2">
                <FaSearch />
              </button>
            </li>
          </ul>
        </div>
      </div>

      {searchOpen && (
        <div className="search-container">
          <form className="d-flex justify-content-center mt-3" onSubmit={handleSearch}>
            <input
              className="form-control me-2 search-input"
              type="search"
              placeholder="Search by city..."
              aria-label="Search"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button className="btn btn-outline-success" type="submit" disabled={isLoading}>
              {isLoading ? <FaSpinner className="spinner" /> : <FaSearch />}
            </button>
          </form>
          {error && <p className="text-danger text-center mt-2">{error}</p>}

          <div className="container mt-4">
            <div className="row">
              {searchResults.hotels.length > 0 && (
                <div className="col-12">
                  <h3 className="text-primary mb-4">Hotels</h3>
                  <div className="row">
                    {searchResults.hotels.map((hotel) => (
                      <div className="col-md-4 mb-4" key={hotel._id}>
                        <div className="card shadow-sm hotel-card">
                          <div className="card-body">
                            <h5 className="card-title">{hotel.name}</h5>
                            <p className="card-text">Rating: {hotel.rating} ⭐</p>
                            <p className="card-text">{hotel.location}</p>
                            <Link to={`/hotels/${hotel._id}`} className="btn btn-primary">View Details</Link>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                  <Link to={`/search/hotels?city=${searchQuery}`} className="btn btn-link">View all hotels</Link>
                </div>
              )}

              {searchResults.restaurants.length > 0 && (
                <div className="col-12">
                  <h3 className="text-primary mb-4">Restaurants</h3>
                  <div className="row">
                    {searchResults.restaurants.map((restaurant) => (
                      <div className="col-md-4 mb-4" key={restaurant._id}>
                        <div className="card shadow-sm restaurant-card">
                          <div className="card-body">
                            <h5 className="card-title">{restaurant.name}</h5>
                            <p className="card-text">Rating: {restaurant.rating} ⭐</p>
                            <p className="card-text">{restaurant.location}</p>
                            <Link to={`/restaurants/${restaurant._id}`} className="btn btn-primary">View Details</Link>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                  <Link to={`/search/restaurants?city=${searchQuery}`} className="btn btn-link">View all restaurants</Link>
                </div>
              )}

              {!isLoading && searchQuery && searchResults.hotels.length === 0 && searchResults.restaurants.length === 0 && (
                <p className="text-center">No results found for "{searchQuery}"</p>
              )}
            </div>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
