import React, { useState } from 'react';
import './MiloChatbot.css';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [isChatVisible, setIsChatVisible] = useState(false);

  const handleSendMessage = () => {
    if (userInput.trim() === '') return;

    const newMessage = {
      text: userInput,
      sender: 'user',
    };

    setMessages([...messages, newMessage]);
    setUserInput('');

    // Simulate bot response (can be extended with actual bot logic)
    setTimeout(() => {
      const botMessage = {
        text: "I'm Milo, your chatbot! How can I assist you today?",
        sender: 'bot',
      };
      setMessages((prevMessages) => [...prevMessages, botMessage]);
    }, 1000);
  };

  return (
    <div className="app">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-brand">Voyaige Chatbot</div>
        <button className="navbar-toggle" onClick={() => setIsChatVisible(!isChatVisible)}>
          {isChatVisible ? 'Close Chat' : 'Open Chat'}
        </button>
      </nav>

      {/* Chatbot Section */}
      {isChatVisible && (
        <div className="chat-container">
          <div className="chat-history">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`chat-bubble ${message.sender}-bubble`}
              >
                {message.text}
              </div>
            ))}
          </div>
          <div className="chat-input-container">
            <input
              type="text"
              className="chat-input"
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
              placeholder="Type a message..."
            />
            <button
              className="send-button"
              onClick={handleSendMessage}
              disabled={!userInput.trim()}
            >
              Send
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
