import React, { useState } from 'react';
import './AiBanner.css';

// Modal Component
const PlanModal = ({ isOpen, onClose }) => {
  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-container">
        <button className="modal-close" onClick={onClose}>×</button>
        <div className="modal-content">
          <div className="modal-option chatbot" onClick={() => window.location.href='/ai-chatbot'}>
            <h2>Plan with AI Chatbot</h2>
            <p>Interactive conversation to plan your perfect trip</p>
          </div>
          <div className="modal-option form-based" onClick={() => window.location.href='/form-recommendation'}>
            <h2>Form Based Recommendation</h2>
            <p>Quick and structured way to get travel suggestions</p>
          </div>
          <div className="diagonal-line"></div>
        </div>
      </div>
    </div>
  );
};

// Main Banner Component
const AiBanner = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  return (
    <div className="ai-trip-banner">
      <div className="banner-content">
        <div className="text-content">
          <div className="ai-badge">Powered by AI <span className="beta-tag">BETA</span></div>
          <h1 className="aimain-title">AI trip builder gets you out there</h1>
          <p className="aisubtitle">Get a whole getaway's worth of ideas made for you—ready in seconds.</p>
          <button className="try-button" onClick={() => setIsModalOpen(true)}>
            <span className="icon">✦</span> Try it
          </button>
        </div>
        <div className="image-container">
          <img src="https://img.eurohike.at/w_314,h_235,q_80,v_49f23c,hash_7fe451/ddcijcnkv/image/upload/v1683199297/eurohike/touren/oesterreich/almwandern-salzkammergut-fuer-familien/neu/eurohike-wanderreise-salzkammergut-genneralm-familie1.jpg" alt="Family hiking together" className="banner-image" />
        </div>
      </div>
      <PlanModal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />
    </div>
  );
};

export default AiBanner;
