import React, { useState } from 'react';
import NavBar from "../NavBar/Navbar";
import axios from 'axios'; // Import Axios for API requests
import './MiloChatbot.css';

const MiloChatbot = () => {
  const [messages, setMessages] = useState([]); // Holds chat messages
  const [userInput, setUserInput] = useState(''); // Holds user input
  const [isLoading, setIsLoading] = useState(false); // Loading state

  // Handles sending the user message to the backend
  const handleSendMessage = async () => {
    if (!userInput.trim()) return;
  
    setMessages((prevMessages) => [
      ...prevMessages,
      { text: userInput, sender: 'user' },
    ]);
  
    setIsLoading(true);
    setUserInput('');
  
    try {
      const response = await axios.post('http://127.0.0.1:5000/chat', {
        message: userInput,
      });
      setMessages((prevMessages) => [
        ...prevMessages,
        { text: response.data.response, sender: 'bot' },
      ]);
    } catch (error) {
      console.error("Error:", error.response ? error.response.data : error.message);
      setMessages((prevMessages) => [
        ...prevMessages,
        { text: "Error connecting to the server.", sender: 'bot' },
      ]);
    } finally {
      setIsLoading(false);
    }
  };
 
  return (
    <div className="chat-fullpage">
      <NavBar />
      <div className="chat-history">
        {/* Render all messages from both the user and bot */}
        {messages.map((message, index) => (
          <div
            key={index}
            className={`chat-bubble ${message.sender === 'user' ? 'user-bubble' : 'bot-bubble'}`}
          >
            {message.text}
          </div>
        ))}
        {/* Show loading indicator while waiting for the bot's response */}
        {isLoading && (
          <div className="chat-bubble bot-bubble">...</div>
        )}
      </div>

      <div className="chat-input-container">
        <input
          type="text"
          className="chat-input"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)} // Update user input state
          placeholder="Type a message..."
        />
        <button
          className="send-button"
          onClick={handleSendMessage} // Send the message on button click
          disabled={isLoading} // Disable the button if the message is being processed
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default MiloChatbot;
