/*
import React from 'react';

const Home = () => {
  const user = JSON.parse(localStorage.getItem('user'));

  return (
    <div>
      {user ? (
        <h1>Welcome, {user.firstName} {user.lastName}</h1>
      ) : (
        <h1>Welcome to Voyaige</h1>
      )}
    </div>
  );
};

export default Home;
*/


import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import "./Homepage.css";

const citiesInPakistan = [
  "Islamabad",
  "Lahore",
  "Karachi",
  "Murree",
  "Peshawar",
  "Skardu",
  "Hunza",
  "Quetta",
  "Multan",
  "Faisalabad",
];

const Homepage = () => {
  const [startingPoint, setStartingPoint] = useState("");
  const [destination, setDestination] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  const navigate = useNavigate(); // Initialize useNavigate for navigation

  const handleSearch = () => {
    // Check if all fields are filled
    if (startingPoint && destination && startDate && endDate) {
      // Navigate to the /results page with query parameters
      navigate(
        `/results?startingPoint=${startingPoint}&destination=${destination}&startDate=${startDate}&endDate=${endDate}`
      );
    } else {
      alert("Please fill in all the fields!");
    }
  };

  return (
    <div className="homepage">
      <section className="hero-section">
        <div className="hero-overlay">
          <div className="hero-content">
            <h1>Explore Beautiful Cities in Pakistan</h1>
            <p>Discover the beauty of Pakistan with Voyaige Travel</p>

            <div className="search-form">
              <select
                value={startingPoint}
                onChange={(e) => setStartingPoint(e.target.value)}
                className="dropdown"
              >
                <option value="" disabled>
                  Starting Point
                </option>
                {citiesInPakistan.map((city) => (
                  <option key={city} value={city}>
                    {city}
                  </option>
                ))}
              </select>

              <select
                value={destination}
                onChange={(e) => setDestination(e.target.value)}
                className="dropdown"
              >
                <option value="" disabled>
                  Destination
                </option>
                {citiesInPakistan.map((city) => (
                  <option key={city} value={city}>
                    {city}
                  </option>
                ))}
              </select>

              <input
                type="date"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
                className="date-picker"
              />
              <input
                type="date"
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
                className="date-picker"
              />

              <button onClick={handleSearch} className="search-button">
                Search
              </button>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Homepage;
