import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Navbar from '../../NavBar/Navbar';
import './UserCustomization.css';

const UserCustomization = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { customizationData, bookingData } = location.state || {};

  // Extract trip basic information from customizationData with defaults
  const {
    startingPoint = '',
    destination = '',
    startDate = '',
    endDate = '',
    guests = 1
  } = customizationData || {};

  // Create a unique trip ID based on trip details to use as storage key
  // This ensures different trips have separate storage
  const getTripStorageKey = () => {
    if (!startingPoint || !destination || !startDate || !endDate) return null;
    return `trip_${startingPoint}_${destination}_${startDate}_${endDate}_${guests}`;
  };

  const tripStorageKey = getTripStorageKey();

  // Basic trip information states (non-editable)
  const [selectedStartingPoint] = useState(startingPoint);
  const [selectedDestination] = useState(destination);
  const [selectedStartDate] = useState(startDate);
  const [selectedEndDate] = useState(endDate);
  const [selectedGuests] = useState(guests);
  const [numberOfDays, setNumberOfDays] = useState(0);
  
  // Trip-level details for hotel and transport
  const [tripDetails, setTripDetails] = useState({
    hotel: null,
    transport: null
  });

  // State to store food and attractions details for each day
  const [dayDetails, setDayDetails] = useState({});

  // Define food and attraction options for different cities
  const cityOptions = {
    "Islamabad": {
      lunch: ["Monal Restaurant", "Tandoori Restaurant", "Chaaye Khana"],
      dinner: ["Savour Foods", "Kabul Restaurant", "Dynasty Restaurant"],
      attractions: ["Faisal Mosque", "Daman-e-Koh", "Pakistan Monument"]
    },
    "Lahore": {
      lunch: ["Haveli Restaurant", "Andaaz Restaurant", "Spice Bazaar"],
      dinner: ["Cooco's Den", "Village Restaurant", "Fujiyama Restaurant"],
      attractions: ["Badshahi Mosque", "Lahore Fort", "Shalimar Gardens"]
    },
    "Karachi": {
      lunch: ["BBQ Tonight", "Kolachi Restaurant", "Kababjees"],
      dinner: ["Port Grand", "Okra Restaurant", "Cafe Flo"],
      attractions: ["Clifton Beach", "Mazar-e-Quaid", "Mohatta Palace"]
    },
    // Default options for any city not in the list
    "Default": {
      lunch: ["Local Restaurant 1", "Local Restaurant 2", "Local Restaurant 3"],
      dinner: ["Evening Dining 1", "Evening Dining 2", "Evening Dining 3"],
      attractions: ["Local Attraction 1", "Local Attraction 2", "Local Attraction 3"]
    }
  };

  // Calculate number of days for the trip
  useEffect(() => {
    if (selectedStartDate && selectedEndDate) {
      const start = new Date(selectedStartDate);
      const end = new Date(selectedEndDate);
      const timeDiff = end - start;
      const daysDiff = Math.ceil(timeDiff / (1000 * 60 * 60 * 24)) + 1;
      setNumberOfDays(daysDiff > 0 ? daysDiff : 0);
    }
  }, [selectedStartDate, selectedEndDate]);

  // Load trip details from localStorage based on unique trip key
  useEffect(() => {
    // Only attempt to load if we have a valid storage key
    if (tripStorageKey) {
      // Load trip-specific details (hotel and transport)
      const savedTripDetails = localStorage.getItem(`tripDetails_${tripStorageKey}`);
      if (savedTripDetails) {
        try {
          setTripDetails(JSON.parse(savedTripDetails));
        } catch (error) {
          console.error("Error parsing trip details from localStorage:", error);
          localStorage.removeItem(`tripDetails_${tripStorageKey}`);
        }
      }

      // Load day-specific details (food and attractions)
      const savedDayDetails = localStorage.getItem(`dayDetails_${tripStorageKey}`);
      if (savedDayDetails) {
        try {
          setDayDetails(JSON.parse(savedDayDetails));
        } catch (error) {
          console.error("Error parsing day details from localStorage:", error);
          localStorage.removeItem(`dayDetails_${tripStorageKey}`);
        }
      }
    }
  }, [tripStorageKey]);

  // Update localStorage whenever tripDetails state changes
  useEffect(() => {
    if (tripStorageKey && (tripDetails.hotel || tripDetails.transport)) {
      localStorage.setItem(`tripDetails_${tripStorageKey}`, JSON.stringify(tripDetails));
    }
  }, [tripDetails, tripStorageKey]);

  // Update localStorage whenever dayDetails state changes
  useEffect(() => {
    if (tripStorageKey && Object.keys(dayDetails).length > 0) {
      localStorage.setItem(`dayDetails_${tripStorageKey}`, JSON.stringify(dayDetails));
    }
  }, [dayDetails, tripStorageKey]);

  // Process booking data when coming back from BookOption page
  useEffect(() => {
    if (bookingData && tripStorageKey) {
      console.log("Received Booking Data:", bookingData);
      const { selectedHotel, selectedTransport } = bookingData;
      
      setTripDetails(prevDetails => {
        const updatedDetails = {
          ...prevDetails,
          hotel: selectedHotel || prevDetails.hotel,
          transport: selectedTransport || prevDetails.transport
        };
        
        // Save to trip-specific localStorage immediately
        localStorage.setItem(`tripDetails_${tripStorageKey}`, JSON.stringify(updatedDetails));
        return updatedDetails;
      });
    }
  }, [bookingData, tripStorageKey]);

  // Navigate to BookOption page for editing hotel/transport
  const handleEditTripDetails = () => {
    navigate('/bookoption', { 
      state: { 
        customizationData,
        currentDetails: tripDetails
      } 
    });
  };

  // Handle change for lunch, dinner, and attractions
  const handleDetailChange = (day, type, value) => {
    if (!tripStorageKey) return;
    
    setDayDetails(prevDetails => {
      const updatedDetails = {
        ...prevDetails,
        [day]: {
          ...prevDetails[day],
          [type]: value
        }
      };
      
      // Save to trip-specific localStorage
      localStorage.setItem(`dayDetails_${tripStorageKey}`, JSON.stringify(updatedDetails));
      return updatedDetails;
    });
  };

  // Helper function to safely access nested properties
  const getPropertyValue = (obj, property, fallback = 'N/A') => {
    if (!obj) return fallback;
    
    const properties = property.split('.');
    let value = obj;
    
    for (const prop of properties) {
      if (value && typeof value === 'object' && prop in value) {
        value = value[prop];
      } else {
        return fallback;
      }
    }
    
    return value || fallback;
  };

  // Format date to be more readable
  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      weekday: 'short', 
      month: 'short', 
      day: 'numeric', 
      year: 'numeric' 
    });
  };

  // Get options for the current destination
  const getOptionsForDestination = () => {
    return cityOptions[selectedDestination] || cityOptions["Default"];
  };

  // Calculate total cost including food and attractions
  const calculateTotalCost = () => {
    let totalCost = 0;
    let hotelCost = 0;
    let transportCost = 0;
    let foodCost = 0;
    let attractionsCost = 0;
    
    // Fixed costs for hotel and transport (per trip)
    if (tripDetails.hotel && tripDetails.hotel.price) {
      const price = parseFloat(tripDetails.hotel.price);
      if (!isNaN(price)) {
        hotelCost = price;
        totalCost += price;
      }
    }
    
    if (tripDetails.transport && tripDetails.transport.price) {
      const price = parseFloat(tripDetails.transport.price);
      if (!isNaN(price)) {
        transportCost = price;
        totalCost += price;
      }
    }
    
    // Assumed costs for food and attractions
    const LUNCH_COST = 1000; // PKR
    const DINNER_COST = 1500; // PKR
    const ATTRACTION_COST = 500; // PKR per person
    
    // Calculate daily food and attraction costs
    Object.values(dayDetails).forEach(day => {
      // Add lunch cost if selected
      if (day.lunch) {
        const lunchFee = LUNCH_COST;
        foodCost += lunchFee;
        totalCost += lunchFee;
      }
      
      // Add dinner cost if selected
      if (day.dinner) {
        const dinnerFee = DINNER_COST;
        foodCost += dinnerFee;
        totalCost += dinnerFee;
      }
      
      // Add attractions cost if selected (per person)
      if (day.attraction) {
        const attractionFee = ATTRACTION_COST * selectedGuests;
        attractionsCost += attractionFee;
        totalCost += attractionFee;
      }
    });
    
    return { totalCost, hotelCost, transportCost, foodCost, attractionsCost };
  };

  // Create an array of days for the trip duration
  const getDaysArray = () => {
    const days = [];
    for (let i = 0; i < numberOfDays; i++) {
      const dayNumber = i + 1;
      const dayDate = new Date(selectedStartDate);
      dayDate.setDate(dayDate.getDate() + i);
      days.push({ dayNumber, dayDate });
    }
    return days;
  };

  // Build itinerary day details for checkout
  const buildItineraryDays = () => {
    return getDaysArray().map(({ dayNumber }) => {
      const details = dayDetails[dayNumber] || {};
      const dayTitle = `Day ${dayNumber}: ${getPropertyValue(tripDetails.transport, 'type', 'Trip')} + Hotel`;
      
      // Calculate day cost
      let dayCost = 0;
      
      // Add hotel cost (divided by number of days)
      if (tripDetails.hotel && tripDetails.hotel.price) {
        const hotelPrice = parseFloat(tripDetails.hotel.price);
        if (!isNaN(hotelPrice)) {
          dayCost += hotelPrice / numberOfDays;
        }
      }
      
      // Add transport cost (divided by number of days)
      if (tripDetails.transport && tripDetails.transport.price) {
        const transportPrice = parseFloat(tripDetails.transport.price);
        if (!isNaN(transportPrice)) {
          dayCost += transportPrice / numberOfDays;
        }
      }
      
      // Add food and attraction costs
      if (details.lunch) dayCost += 1000; // LUNCH_COST
      if (details.dinner) dayCost += 1500; // DINNER_COST
      if (details.attraction) dayCost += 500 * selectedGuests; // ATTRACTION_COST * guests
      
      return {
        dayNumber,
        title: dayTitle,
        transportMode: getPropertyValue(tripDetails.transport, 'type', 'Transport'),
        hotel: getPropertyValue(tripDetails.hotel, 'name', 'Hotel'),
        lunch: details.lunch || null,
        dinner: details.dinner || null,
        attraction: details.attraction || null,
        totalCost: Math.round(dayCost) // Round to nearest integer
      };
    });
  };

  // Handle proceed to checkout
  const handleProceedToCheckout = () => {
    if (!tripStorageKey) {
      alert("Missing trip details. Please fill all required fields.");
      return;
    }
    
    const { totalCost } = calculateTotalCost();
    
    // Create trip data object for checkout
    const tripData = {
      summary: {
        from: selectedStartingPoint,
        to: selectedDestination,
        startDate: selectedStartDate,
        endDate: selectedEndDate,
        guests: selectedGuests,
        duration: numberOfDays
      },
      days: buildItineraryDays(),
      discountPercentage: 0, // Default no discount, can be calculated based on user points
      totalCost
    };
    
    // Save trip data to localStorage for checkout page
    localStorage.setItem('tripCheckoutData', JSON.stringify(tripData));
    
    // Store the current trip key so checkout knows which trip this is
    localStorage.setItem('currentTripKey', tripStorageKey);
    
    // Navigate to checkout page
    navigate('/checkout');
  };

  // Determine if user has selected required trip elements
  const isTripComplete = tripDetails.hotel && tripDetails.transport && numberOfDays > 0;

  // Translate guest number to budget type for display
  const getBudgetType = (guests) => {
    switch(guests) {
      case 1: return "Economic";
      case 2: return "Normal";
      case 3: return "Luxury";
      default: return "N/A";
    }
  };


};
