import React, { useState } from 'react';


const MiloChatbot = () => {
    const [userInput, setUserInput] = useState("");
    const [chatbotResponse, setChatbotResponse] = useState("");
    const [isLoading, setIsLoading] = useState(false);

    const handleInputChange = (e) => {
        setUserInput(e.target.value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        try {
            const response = await fetch('http://localhost:5000/MiloChatbot', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ user_input: userInput }),
            });

            const data = await response.json();
            setChatbotResponse(data.response || 'Error: Unable to process your request.');
        } catch (error) {
            console.error('Error:', error);
            setChatbotResponse('Error occurred while contacting the server.');
        } finally {
            setIsLoading(false);
            setUserInput(""); // Clear the input field
        }
    };

    return (
        <div className="chatbot">
            <h2>Welcome to Milo, Your Travel Assistant</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={userInput}
                    onChange={handleInputChange}
                    placeholder="Ask me anything about your trip..."
                />
                <button type="submit">Ask</button>
            </form>
            {isLoading ? (
                <p>Loading...</p>
            ) : (
                <div>
                    <h3>Response:</h3>
                    <p>{chatbotResponse}</p>
                </div>
            )}
        </div>
    );
};

export default MiloChatbot;
