import React, { useState, useEffect } from "react";
import "./MiloChatbot.css";

const MiloChatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [welcomeVisible, setWelcomeVisible] = useState(true);
  const [history, setHistory] = useState([]);
  const [historyIndex, setHistoryIndex] = useState(-1);

  const apiBaseURL = "http://127.0.0.1:5000"; // Replace with your Flask backend's URL

  const sendToBackend = async (endpoint, payload) => {
    try {
      const controller = new AbortController();
      const timeout = setTimeout(() => controller.abort(), 5000); // Timeout after 5 seconds

      const response = await fetch(`${apiBaseURL}${endpoint}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
        signal: controller.signal, // Attach abort signal
      });

      clearTimeout(timeout); // Clear timeout if request succeeds

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      return data.response || data.result;
    } catch (error) {
      if (error.name === "AbortError") {
        console.error("Fetch request timed out");
      } else {
        console.error("Error communicating with backend:", error);
      }
      return "Sorry, something went wrong. Please try again later.";
    }
  };

  const handleUserMessage = async () => {
    if (!input.trim()) return "Error";

    const userMessage = input.trim();
    setMessages([...messages, { sender: "user", text: userMessage }]);
    setHistory([userMessage, ...history]);
    setInput("");
    setWelcomeVisible(false);

    // Determine endpoint and payload
    let endpoint = "/MiloChatbot";
    let payload = { user_input: userMessage };

    if (userMessage.toLowerCase().includes("generate itinerary")) {
      endpoint = "/generate-itinerary";
      payload = {
        user_input: userMessage,
        starting_city: "Islamabad", // Replace with dynamic user data
        destination_city: "Lahore", // Replace with dynamic user data
        days: 3,
        mode_of_transport: "bus",
        budget: 50000,
      };
    } else if (userMessage.toLowerCase().includes("tell me about")) {
      endpoint = "/retrieve-details";
    } else if (userMessage.toLowerCase().includes("top")) {
      endpoint = "/retrieve-top-items";
      payload.num_recommendations = 5; // Replace with extracted number from user input
    } else if (userMessage.toLowerCase().includes("compare")) {
      endpoint = "/compare-two-options";
    }

    // Get response from backend
    const botResponse = await sendToBackend(endpoint, payload);
    setMessages((prevMessages) => [
      ...prevMessages,
      { sender: "milo", text: botResponse },
    ]);
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleUserMessage();
    } else if (e.key === "ArrowUp") {
      if (history.length > 0 && historyIndex < history.length - 1) {
        const newIndex = historyIndex + 1;
        setInput(history[newIndex]);
        setHistoryIndex(newIndex);
      }
    } else if (e.key === "ArrowDown") {
      if (historyIndex > 0) {
        const newIndex = historyIndex - 1;
        setInput(history[newIndex]);
        setHistoryIndex(newIndex);
      } else {
        setInput("");
        setHistoryIndex(-1);
      }
    }
  };

  useEffect(() => {
    const chatContainer = document.getElementById("chat-container");
    if (chatContainer) {
      chatContainer.scrollTop = chatContainer.scrollHeight;
    }
  }, [messages]);

  return (
    <div className="chatbot-container">
      {welcomeVisible && (
        <div className="welcome-message">
          <p>Welcome to a chat with Milo, your own personal smart AI trip planner.</p>
          <p>Click on the input bar to start the chat.</p>
        </div>
      )}

      <div id="chat-container" className="chat-container">
        {!welcomeVisible && (
          <div className="chat-bubble milo">
            <span>Milo: Hi, how may I help you plan your trip?</span>
          </div>
        )}
        {messages.map((msg, index) => (
          <div key={index} className={`chat-bubble ${msg.sender}`}>
            <span>{msg.sender === "user" ? `You: ${msg.text}` : `Milo: ${msg.text}`}</span>
          </div>
        ))}
      </div>

      <div className="input-container">
        <input
          type="text"
          placeholder="Type your message here..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          onFocus={() => setWelcomeVisible(false)}
        />
        <button onClick={handleUserMessage}>Send</button>
      </div>
    </div>
  );
};

export default MiloChatbot;
