import React, { useState, useEffect } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { CreditCard, Lock, Calendar, User, Shield } from 'lucide-react';
import './CheckoutPage.css';
import Navbar from '../NavBar/Navbar';

const CheckoutPage = () => {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [promoCode, setPromoCode] = useState('');
  const [isCardFocused, setIsCardFocused] = useState(false);
  const [tripData, setTripData] = useState({
    days: [],
    discountPercentage: 0
  });
  const [promoDiscount, setPromoDiscount] = useState(0);
  const [validPromo, setValidPromo] = useState(false);

  // Load trip data from localStorage
  useEffect(() => {
    const savedTripData = localStorage.getItem('tripCheckoutData');
    if (savedTripData) {
      try {
        const parsedData = JSON.parse(savedTripData);
        setTripData(parsedData);
      } catch (err) {
        console.error('Error parsing trip data:', err);
        setError('Error loading trip details. Please try again.');
      }
    } else {
      // Fallback data if nothing is in localStorage (for development/testing)
      setTripData({
        days: [
          { title: "Plan + Flight + Hotel", cost: 140000 },
          { title: "Plan + Bus + Hotel", cost: 25000 },
          { title: "Plan + Car + Hotel", cost: 32500 }
        ],
        discountPercentage: 10
      });
    }
  }, []);

  // Calculate totals
  const subtotal = tripData.days.reduce((sum, day) => sum + day.cost, 0);
  const pointsDiscount = (subtotal * tripData.discountPercentage) / 100;
  const total = subtotal - pointsDiscount - promoDiscount;

  useEffect(() => {
    if (success) {
      // Clear trip data from localStorage after successful payment
      localStorage.removeItem('tripCheckoutData');
      
      const timer = setTimeout(() => {
        navigate('/confirmation', { 
          state: { 
            tripData,
            paymentDetails: {
              subtotal,
              pointsDiscount,
              promoDiscount,
              total
            }
          }
        });
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [success, navigate, tripData, subtotal, pointsDiscount, promoDiscount, total]);

  const handlePromoCode = () => {
    // Simple promo code validation logic
    if (promoCode.toUpperCase() === 'TRAVEL10') {
      setPromoDiscount(subtotal * 0.1); // 10% discount
      setValidPromo(true);
    } else if (promoCode.toUpperCase() === 'WELCOME20') {
      setPromoDiscount(subtotal * 0.2); // 20% discount
      setValidPromo(true);
    } else {
      setError('Invalid promo code');
      setValidPromo(false);
      setPromoDiscount(0);
      setTimeout(() => {
        setError(null);
      }, 3000);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    
    if (!stripe || !elements) {
      setError("Stripe is not properly initialized");
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post('/api/payment-intent', {
        amount: total,
        tripDetails: {
          days: tripData.days.length,
          totalCost: total
        }
      });

      if (response.status !== 200) {
        throw new Error(`Error: ${response.data.message}`);
      }
      
      const { clientSecret } = response.data;

      const { error: stripeError, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: elements.getElement(CardElement),
        }
      });

      if (stripeError) {
        setError(stripeError.message);
        setSuccess(false);
      } else if (paymentIntent.status === 'succeeded') {
        setSuccess(true);
        setError(null);
        
        // Save transaction details to database (would typically be handled server-side)
        try {
          await axios.post('/api/transactions', {
            tripData,
            paymentDetails: {
              paymentId: paymentIntent.id,
              amount: total,
              date: new Date().toISOString()
            }
          });
        } catch (err) {
          console.error('Error saving transaction:', err);
          // Continue with success flow even if transaction recording fails
        }
      } else {
        setError(`Payment status: ${paymentIntent.status}. Please try again.`);
        setSuccess(false);
      }
    } catch (err) {
      setError(err.message || "An error occurred while processing your payment");
      setSuccess(false);
    }
    setLoading(false);
  };

  return (
    <div className="checkout-container">
      <Navbar />
      <div className="checkout-content fade-in">
        <div className="checkout-header">
          <h1>Complete Your Payment</h1>
          <div className="secure-badge">
            <Shield size={18} /> Secured & Encrypted
          </div>
        </div>

        <div className="checkout-grid">
          <div className="summary-card">
            <div className="card-header">
              <h2>Trip Summary</h2>
              <CreditCard size={20} />
            </div>

            <div className="cost-breakdown">
              {tripData.days.map((day, index) => (
                <div key={index} className="cost-item slide-in">
                  <span>Day {index + 1}: {day.title}</span>
                  <span className="cost">{day.cost.toLocaleString()}</span>
                </div>
              ))}
              
              <div className="cost-divider"></div>
              
              <div className="subtotal cost-item">
                <span>Sub-total</span>
                <span className="cost">{subtotal.toLocaleString()}</span>
              </div>
              
              {tripData.discountPercentage > 0 && (
                <div className="discount cost-item">
                  <span>Points Discount ({tripData.discountPercentage}%)</span>
                  <span className="cost-discount">-{pointsDiscount.toLocaleString()}</span>
                </div>
              )}
              
              {validPromo && promoDiscount > 0 && (
                <div className="discount cost-item">
                  <span>Promo Discount ({promoCode.toUpperCase()})</span>
                  <span className="cost-discount">-{promoDiscount.toLocaleString()}</span>
                </div>
              )}
              
              <div className="total cost-item">
                <span>Total Amount</span>
                <span className="cost-total">{total.toLocaleString()}</span>
              </div>
            </div>

            <div className="promosection">
              <h3>Promo Code</h3>
              <div className="promo-input">
                <input 
                  type="text" 
                  value={promoCode}
                  onChange={(e) => setPromoCode(e.target.value)}
                  placeholder="Enter your code"
                />
                <button 
                  type="button"
                  className="promo-button"
                  onClick={handlePromoCode}
                >
                  Apply
                </button>
              </div>
              {validPromo && (
                <div className="promo-success">Promo code applied successfully!</div>
              )}
            </div>
          </div>

          <div className="payment-card">
            <div className="card-header">
              <h2>Card Payment</h2>
              <Lock size={20} />
            </div>

            <form onSubmit={handleSubmit} className="payment-form">
              <div className={`stripe-card ${isCardFocused ? 'focused' : ''}`}>
                <CardElement 
                  onFocus={() => setIsCardFocused(true)}
                  onBlur={() => setIsCardFocused(false)}
                  options={{
                    style: {
                      base: {
                        fontSize: '16px',
                        fontWeight: '500',
                        fontFamily: 'Inter, system-ui, sans-serif',
                        color: '#1a1f36',
                        '::placeholder': {
                          color: '#6b7280',
                        },
                        padding: '20px 0',
                      },
                      invalid: {
                        color: '#ef4444',
                      },
                    },
                  }}
                />
              </div>

              {error && <div className="error-message slide-in">{error}</div>}
              {success && <div className="success-message slide-in">Payment successful! Redirecting to confirmation...</div>}

              <button 
                className={`payment-button ${loading ? 'loading' : ''}`}
                type="submit"
                disabled={!stripe || loading || tripData.days.length === 0}
              >
                {loading ? (
                  <>
                    <div className="spinner"></div>
                    Processing Payment...
                  </>
                ) : (
                  <>
                    <Lock size={18} />
                    Pay {total.toLocaleString()}
                  </>
                )}
              </button>
            </form>

            <div className="security-info">
              <Shield size={16} />
              <span>Your payment information is encrypted and secure</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CheckoutPage;
