import React, { useState, useEffect } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { CreditCard, Lock, Calendar, User } from 'lucide-react';
import './CheckoutPage.css';

const CheckoutPage = ({ tripData = {
  days: [
    { title: "Plan + Flight + Hotel", cost: 140000 },
    { title: "Plan + Bus + Hotel", cost: 25000 },
    { title: "Plan + Car + Hotel", cost: 32500 }
  ],
  discountPercentage: 10
} }) => {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [promoCode, setPromoCode] = useState('');
  const [cardDetails, setCardDetails] = useState({
    number: '',
    name: '',
    expiry: '',
    cvc: ''
  });

  // Calculate totals
  const subtotal = tripData.days.reduce((sum, day) => sum + day.cost, 0);
  const discount = (subtotal * tripData.discountPercentage) / 100;
  const total = subtotal - discount;

  const handleCardNumberChange = (e) => {
    let value = e.target.value.replace(/\s/g, '');
    if (value.length > 16) return;
    
    // Add space after every 4 digits
    value = value.replace(/(\d{4})/g, '$1 ').trim();
    setCardDetails({ ...cardDetails, number: value });
  };

  const handleExpiryChange = (e) => {
    let value = e.target.value.replace(/\D/g, '');
    if (value.length > 4) return;
    
    if (value.length >= 2) {
      value = value.slice(0, 2) + '/' + value.slice(2);
    }
    setCardDetails({ ...cardDetails, expiry: value });
  };

  const handleCVCChange = (e) => {
    let value = e.target.value.replace(/\D/g, '');
    if (value.length > 3) return;
    setCardDetails({ ...cardDetails, cvc: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    
    if (!stripe || !elements) return;

    try {
      const { data: { clientSecret } } = await axios.post('http://localhost:5000/create-payment-intent', {
        amount: total
      });

      const { error: stripeError } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: elements.getElement(CardElement),
        }
      });

      if (stripeError) {
        setError(stripeError.message);
        setSuccess(false);
      } else {
        setSuccess(true);
        setError(null);
      }
    } catch (err) {
      setError(err.message);
      setSuccess(false);
    }
    setLoading(false);
  };

  return (
    <div className="payment-container">
      <div className="payment-header">
        <h1>Complete Your Payment</h1>
        <div className="secure-badge">
          <Lock size={16} /> Secure Payment
        </div>
      </div>

      <div className="payment-content">
        <div className="payment-details">
          <div className="plan-summary">
            <h2>Trip Summary</h2>
            <div className="cost-breakdown">
              {tripData.days.map((day, index) => (
                <div key={index} className="cost-row">
                  <span>Day {index + 1}: {day.title}</span>
                  <span>{day.cost.toLocaleString()}/-</span>
                </div>
              ))}
              <div className="subtotal cost-row">
                <span>Sub-total:</span>
                <span>{subtotal.toLocaleString()}/-</span>
              </div>
              <div className="discount cost-row">
                <span>Points Discount:</span>
                <span>{tripData.discountPercentage}%</span>
              </div>
              <div className="total cost-row">
                <span>TOTAL COST:</span>
                <span>{total.toLocaleString()}/-</span>
              </div>
            </div>

            <div className="promo-section">
              <h3>Have a Promo Code?</h3>
              <div className="promo-input">
                <input 
                  type="text" 
                  value={promoCode}
                  onChange={(e) => setPromoCode(e.target.value)}
                  placeholder="Enter promo code"
                />
                <button className="apply-btn">Apply</button>
              </div>
            </div>
          </div>
        </div>

        <div className="card-payment-section">
          <h2>Card Payment</h2>
          <form onSubmit={handleSubmit} className="card-form">
        
              {    /* <label>
                <CreditCard size={18} />
                Card Number
              </label>
              <input
                type="text"
                placeholder="1234 5678 9012 3456"
                value={cardDetails.number}
                onChange={handleCardNumberChange}
                maxLength="19"
              />
            </div>

            <div className="form-group">
              <label>
                <User size={18} />
                Cardholder Name
              </label>
              <input
                type="text"
                placeholder="Name on card"
                value={cardDetails.name}
                onChange={(e) => setCardDetails({ ...cardDetails, name: e.target.value })}
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>
                  <Calendar size={18} />
                  Expiry Date
                </label>
                <input
                  type="text"
                  placeholder="MM/YY"
                  value={cardDetails.expiry}
                  onChange={handleExpiryChange}
                  maxLength="5"
                />
              </div>

              <div className="form-group">
                <label>
                  <Lock size={18} />
                  CVC
                </label>
                <input
                  type="text"
                  placeholder="123"
                  value={cardDetails.cvc}
                  onChange={handleCVCChange}
                  maxLength="3"
                />
              </div>
            </div> */}

            <div className="stripe-element">
              <CardElement 
                options={{
                  style: {
                    base: {
                      fontSize: '16px',
                      color: '#424770',
                      '::placeholder': {
                        color: '#aab7c4',
                      },
                    },
                    invalid: {
                      color: '#9e2146',
                    },
                  },
                }}
              />
            </div>
            <h6 style={"font_size=28"}>Secured by Stripe</h6>
            {error && <div className="error-message">{error}</div>}
            {success && <div className="success-message">Payment successful!</div>}

            <button 
              className="pay-button" 
              type="submit"
              disabled={!stripe || loading}
            >
              
              {loading ? (
                <>
                  <span className="spinner"></span>
                  Processing...
                </>
              ) : (
                <>
                  <Lock size={16} />
                  Pay {total.toLocaleString()}/-
                </>
              )}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default CheckoutPage;
