import React, { useState, useEffect } from 'react';
import './HeroSection.css';
import Typed from 'typed.js'; // Import Typed.js

const citiesInPakistan = [
  "Islamabad", "Lahore", "Karachi", "Murree", "Peshawar",
  "Skardu", "Hunza", "Quetta", "Multan", "Faisalabad",
];

const images = [
  'https://images8.alphacoders.com/422/422588.jpg',
  'https://www.wns.co.za/Portals/0/Images/HeaderBanner/desktop/1087/53/travel_HD.jpg',
  'https://firstta.com/images/reserval.png'
];

const HeroSection = () => {
  const [startingPoint, setStartingPoint] = useState('');
  const [destination, setDestination] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [guests, setGuests] = useState(1);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const options = {
      strings: ["Discover Pakistan's Hidden Gems", "Your Personal AI Trip Planner", "Plan Your Next Adventure"],
      typeSpeed: 60,
      backSpeed: 30,
      loop: true,
    };
    const typed = new Typed(".tagline", options);
    return () => {
      typed.destroy();
    };
  }, []);

  // Auto change images for the slider
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000); // Change every 5 seconds
    return () => clearInterval(interval);
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    console.log('Search:', { startingPoint, destination, startDate, endDate, guests });
  };

  return (
    <div className="hero-section">
      <div className="slider">
        {images.map((image, index) => (
          <div
            key={index}
            className={`slide ${index === currentImageIndex ? 'active' : ''}`}
            style={{ backgroundImage: `url(${image})` }}
          ></div>
        ))}
      </div>
      <div className="hero-content">
        <div className="blurred-background"></div>
        <h1 className="main-title">Voyaige</h1>
        <h2 className="subtitle">Your Personal AI Trip Planner</h2>
        {/* This is where Typed.js will animate the text */}
      </div>
      <form onSubmit={handleSearch} className="search-form">
        <div className="search-row">
          <div className="search-input">
            <i className="fas fa-map-marker-alt"></i>
            <select
              value={startingPoint}
              onChange={(e) => setStartingPoint(e.target.value)}
              placeholder="Starting Point"
            >
              <option value="" disabled>Starting Point</option>
              {citiesInPakistan.map((city, index) => (
                <option key={index} value={city}>{city}</option>
              ))}
            </select>
          </div>
          <div className="search-input">
            <i className="fas fa-map-marker-alt"></i>
            <select
              value={destination}
              onChange={(e) => setDestination(e.target.value)}
              placeholder="Where are you going?"
            >
              <option value="" disabled>Destination</option>
              {citiesInPakistan.map((city, index) => (
                <option key={index} value={city}>{city}</option>
              ))}
            </select>
          </div>
          <div className="search-input">
            <i className="far fa-calendar-alt"></i>
            <input
              type="date"
              placeholder="Start Date"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
            />
          </div>
          <div className="search-input">
            <i className="far fa-calendar-alt"></i>
            <input
              type="date"
              placeholder="End Date"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
            />
          </div>
          <div className="search-input">
            <i className="fas fa-user-friends"></i>
            <select
              value={guests}
              onChange={(e) => setGuests(Number(e.target.value))}
            >
              <option value="1">1 Person</option>
              <option value="2">2 Persons</option>
              <option value="3">3 Persons</option>
              <option value="4">4+ Persons</option>
            </select>
          </div>
        </div>
        <button type="submit" className="search-button">
          <span>Plan Now</span>
          <i className="fas fa-paper-plane"></i>
        </button>
      </form>
    </div>
  );
};

export default HeroSection;
