import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Profile.css";
import Navbar from "../NavBar/Navbar";

const Profile = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [user, setUser] = useState({});
  const [itineraries, setItineraries] = useState([]);
  const [totalPoints, setTotalPoints] = useState(0);
  const [vouchers, setVouchers] = useState([
    { name: "10% Off", requiredPoints: 100, isRedeemed: false },
    { name: "Free Meal", requiredPoints: 200, isRedeemed: false },
    { name: "Airport Lounge Access", requiredPoints: 300, isRedeemed: false },
  ]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch user details
        const userResponse = await axios.get("http://localhost:5000/api/users", {
          withCredentials: true, // Ensure credentials are sent for authentication
        });
  
        if (!userResponse.data || Object.keys(userResponse.data).length === 0) {
          throw new Error("No logged-in user found");
        }
  
        setUser(userResponse.data);
        console.log("User Data:", userResponse.data);
  
        // Fetch user-specific itineraries
        const itinerariesResponse = await axios.get(
          `http://localhost:5000/api/trips?userId=${userResponse.data._id}`
        );
  
        setItineraries(itinerariesResponse.data);
  
        // Calculate points from completed trips
        const pointsEarned = itinerariesResponse.data.length * 50;
        setTotalPoints(pointsEarned);
      } catch (error) {
        console.error("Error fetching data:", error.message);
        alert(error.message); // Show error to user
      }
    };
  
    fetchData();
  }, []);
  
  const handleEditToggle = () => setIsEditing(!isEditing);

  const handleInputChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleRedeem = (index) => {
    if (totalPoints >= vouchers[index].requiredPoints) {
      const updatedVouchers = [...vouchers];
      updatedVouchers[index].isRedeemed = true;
      setVouchers(updatedVouchers);
      alert("üéâ Voucher Redeemed!");
    } else {
      alert("‚ùå Not enough points.");
    }
  };

  return (
    <div className="profile-container">
      <Navbar />
      <div className="profile-content">
        <h2 className="profile-title">User Profile</h2>

        {/* Personal Details Section */}
        <div className="profile-section">
          <h3>Personal Details</h3>
          <div className="profile-details">
            {Object.keys(user).map((key) => (
              <div className="profile-field" key={key}>
                <label>{key.replace(/([A-Z])/g, " $1")}:</label>
                <input
                  type="text"
                  name={key}
                  value={user[key] || ""}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                  className="profile-input"
                />
              </div>
            ))}
            <button className="edit-btn" onClick={handleEditToggle}>
              {isEditing ? "Save" : "Edit"}
            </button>
          </div>
        </div>

        {/* Itineraries Section */}
        <div className="profile-section">
          <h3>Past Itineraries</h3>
          <div className="itineraries">
            {itineraries.length > 0 ? (
              itineraries.map((trip, index) => (
                <div key={index} className="itinerary-card">
                  <p><strong>From:</strong> {trip.start}</p>
                  <p><strong>To:</strong> {trip.destination}</p>
                  <p><strong>Date:</strong> {trip.date}</p>
                </div>
              ))
            ) : (
              <p>No itineraries available.</p>
            )}
          </div>
        </div>

        {/* Points Section */}
        <div className="profile-section">
          <h3>Total Points</h3>
          <p><strong>{totalPoints}</strong></p>
        </div>

        {/* Vouchers Section */}
        <div className="profile-section">
          <h3>Vouchers</h3>
          <div className="vouchers">
            {vouchers.map((voucher, index) => (
              <div key={index} className="voucher-card">
                <p><strong>{voucher.name}</strong></p>
                <p>Required Points: {voucher.requiredPoints}</p>
                {!voucher.isRedeemed ? (
                  <button onClick={() => handleRedeem(index)}>Redeem</button>
                ) : (
                  <div className="redeemed-voucher">
                    üéä Code: <strong>{Math.random().toString(36).substr(2, 8).toUpperCase()}</strong> üéä
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
