import React, { useState, useEffect } from "react";
import hotelData from "./clean_hotel_data.json";
import "./Hotels.css";
import NavBar from "../"

const Hotels = () => {
  const [hotelsByCity, setHotelsByCity] = useState({});

  useEffect(() => {
    const groupedHotels = hotelData.reduce((acc, hotel) => {
      const { city } = hotel;
      if (!acc[city]) acc[city] = [];
      if (acc[city].length < 5) acc[city].push(hotel);
      return acc;
    }, {});
    setHotelsByCity(groupedHotels);
  }, []);

  return (
    <div className="hotels-container">
      {Object.keys(hotelsByCity).map((city) => (
        <div key={city} className="city-section">
          <h2>Hotels in {city}</h2>
          <div className="hotels-grid">
            {hotelsByCity[city].map((hotel) => (
              <div key={hotel.id} className="hotel-card">
                <img src={hotel.image} alt={hotel.name} className="hotel-image" />
                <h3>{hotel.name}</h3>
                <p>{hotel.rating} ‚≠ê ({hotel.reviews} reviews)</p>
                <p>{hotel.category}</p>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Hotels;
