import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaSearch, FaHotel, FaUtensils, FaMapMarkerAlt, FaWalking } from 'react-icons/fa';
import axios from 'axios';
import './MiloChatbot.css'; // Ensure your chatbot styling is included here
import logo from '../../assets/LOGO.png'; // If you have the logo, adjust the path accordingly

const CITIES = ["Islamabad", "Lahore", "Karachi", "Murree", "Peshawar", "Skardu", "Hunza", "Quetta", "Multan", "Faisalabad"];

const MiloChatbot = () => {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [isChatVisible, setIsChatVisible] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState(null);
  const [error, setError] = useState(null);
  const [searchOpen, setSearchOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const checkAuthentication = () => {
      const token = localStorage.getItem('token');
      setIsAuthenticated(!!token);
    };
    checkAuthentication();
    window.addEventListener('storage', checkAuthentication);
    return () => {
      window.removeEventListener('storage', checkAuthentication);
    };
  }, []);

  const handleSendMessage = () => {
    if (userInput.trim() === '') return;

    const newMessage = {
      text: userInput,
      sender: 'user',
    };

    setMessages([...messages, newMessage]);
    setUserInput('');

    // Simulate bot response (this can be enhanced later with actual bot logic)
    setTimeout(() => {
      const botMessage = {
        text: "I'm Milo, your chatbot! How can I assist you today?",
        sender: 'bot',
      };
      setMessages((prevMessages) => [...prevMessages, botMessage]);
    }, 1000);
  };

  const signOut = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsAuthenticated(false);
    navigate('/login');
  };

  const handleSearch = async (query) => {
    setSearchQuery(query);
    if (query.length >= 2) {
      setError(null);
      const matchedCity = CITIES.find(city => city.toLowerCase().includes(query.toLowerCase()));
      if (matchedCity) {
        try {
          const { data } = await axios.get(`http://localhost:5000/api/search`, {
            params: { query: matchedCity }
          });
          setSearchResults({
            city: {
              name: matchedCity,
              path: `/city/${matchedCity.toLowerCase()}`,
            },
            hotels: data.hotels || [],
            restaurants: data.restaurants || [],
            thingsToDo: data.attractions || []
          });
        } catch (error) {
          console.error('Error fetching search results:', error);
          setError('An error occurred while searching. Please try again.');
        }
      } else {
        setSearchResults(null);
        setError(`No results for "${query}"`);
      }
    } else {
      setSearchResults(null);
      setError(null);
    }
  };

  return (
    <div className="app">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-container">
          <Link to="/" className="navbar-logo">
            <img src={logo} alt="Logo" />
            <span>Voyaige</span>
          </Link>
          <ul className="navbar-menu">
            <li><Link to="/">Home</Link></li>
            <li><Link to="/tours">Tours</Link></li>
            <li><Link to="/hotels">Hotels</Link></li>
            <li><Link to="/restaurants">Eat & Drink</Link></li>
          </ul>
          <div className="navbar-actions">
            <button onClick={() => setSearchOpen(!searchOpen)} className="search-button">
              <FaSearch />
            </button>
            {isAuthenticated ? (
              <button className="auth-button" onClick={signOut}>Log Out</button>
            ) : (
              <Link className="auth-button" to="/login">Sign In</Link>
            )}
          </div>
        </div>
        {searchOpen && (
          <div className="nav-search-overlay">
            <div className="nav-search-box">
              <div className="nav-search-input-container">
                <FaSearch className="nav-search-icon" />
                <input
                  type="text"
                  placeholder="Search cities..."
                  value={searchQuery}
                  onChange={(e) => handleSearch(e.target.value)}
                  className="nav-search-input"
                />
              </div>
              {error && <p className="nav-error-message">{error}</p>}
              {searchResults && searchQuery && !error && (
                <div className="nav-search-recommendations">
                  <div onClick={() => navigate(`/city/${searchResults.city.name.toLowerCase()}`)} className="nav-recommendation-item">
                    <FaMapMarkerAlt className="nav-recommendation-icon" />
                    <div className="nav-recommendation-content">
                      <div className="nav-recommendation-title">{searchResults.city.name}</div>
                      <div className="nav-recommendation-subtitle">City</div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        )}
      </nav>

      {/* Chatbot Section */}
      <div className="chat-container">
        <button className="chat-toggle" onClick={() => setIsChatVisible(!isChatVisible)}>
          {isChatVisible ? 'Close Chat' : 'Open Chat'}
        </button>
        {isChatVisible && (
          <div className="chat-history">
            {messages.map((message, index) => (
              <div key={index} className={`chat-bubble ${message.sender}-bubble`}>
                {message.text}
              </div>
            ))}
          </div>
        )}
        {isChatVisible && (
          <div className="chat-input-container">
            <input
              type="text"
              className="chat-input"
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
              placeholder="Type a message..."
            />
            <button
              className="send-button"
              onClick={handleSendMessage}
              disabled={!userInput.trim()}
            >
              Send
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default MiloChatbot;
