import React, { useState, useRef, useEffect } from "react";
import "./MiloChatbot.css";

const MiloChatbot = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const [itineraryData, setItineraryData] = useState([]);
    const [rightContainerVisible, setRightContainerVisible] = useState(false);
    
    const historyRef = useRef([]);
    const historyIndexRef = useRef(-1);

    const handleUserInput = async () => {
        if (!input.trim()) return;
        
        const userMessage = input.trim();
        setMessages(prev => [...prev, { sender: "user", text: userMessage }]);
        setInput("");
        
        historyRef.current = [userMessage, ...historyRef.current];
        historyIndexRef.current = -1;
        
        let endpoint = "/chatbot";
        let payload = { user_input: userMessage };
        
        if (/\b(plan|generate|create)\s+(a\s+)?(trip|itinerary)\b/i.test(userMessage)) {
            const cityMatch = userMessage.match(/\bfrom\s+(\w+)\s+to\s+(\w+)\b/i) ||
                              userMessage.match(/\bto\s+(\w+)\s+from\s+(\w+)\b/i);

            const startingCity = cityMatch ? cityMatch[2] : "Islamabad";
            const destinationCity = cityMatch ? cityMatch[1] : "Lahore";

            endpoint = "/generate-itinerary";
            payload = { starting_city: startingCity, destination_city: destinationCity };
        }

        try {
            const response = await fetch(`http://127.0.0.1:5000${endpoint}`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(payload),
            });

            const data = await response.json();
            
            if (endpoint === "/generate-itinerary" && Array.isArray(data.messages) && data.messages.length > 0) {
                setItineraryData(data.messages.filter(line => line.trim() !== ""));
                setRightContainerVisible(true);
                setMessages(prev => [...prev, { sender: "milo", text: "Generated itinerary is ready!" }]);
            } else {
                setMessages(prev => [...prev, { sender: "milo", text: data.response || "Sorry, I couldn't generate an itinerary. Please try again." }]);
            }
        } catch (error) {
            setMessages(prev => [...prev, { sender: "milo", text: "An error occurred. Please try again." }]);
        }
    };

    const handleKeyDown = (event) => {
        if (event.key === "Enter") {
            handleUserInput();
        } else if (event.key === "ArrowUp" && historyRef.current.length > 0) {
            historyIndexRef.current = Math.min(historyIndexRef.current + 1, historyRef.current.length - 1);
            setInput(historyRef.current[historyIndexRef.current]);
        } else if (event.key === "ArrowDown" && historyIndexRef.current > 0) {
            historyIndexRef.current--;
            setInput(historyRef.current[historyIndexRef.current]);
        }
    };

    const closeRightContainer = () => {
        setRightContainerVisible(false);
        setItineraryData([]);
    };

    return (
        <div className={`chatbot-container ${rightContainerVisible ? "shift-left" : ""}`}>
            <div className="chat-container">
                <div className="messages">
                    {messages.map((msg, index) => (
                        <div key={index} className={`message ${msg.sender}`}>
                            {msg.text}
                        </div>
                    ))}
                </div>
                <div className="input-area">
                    <input 
                        type="text" 
                        value={input} 
                        onChange={(e) => setInput(e.target.value)} 
                        onKeyDown={handleKeyDown} 
                        placeholder="Type your message..." 
                    />
                    <button onClick={handleUserInput}>Send</button>
                </div>
            </div>

            {rightContainerVisible && (
                <div className="itinerary-container">
                    <button className="close-btn" onClick={closeRightContainer}>âœ–</button>
                    <h3>Generated Itinerary</h3>
                    <ul>
                        {itineraryData.map((line, idx) => <li key={idx}>{line}</li>)}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default MiloChatbot;
