import React, { useState, useEffect } from "react";
import Navbar from "../NavBar/Navbar";
import "./MiloChatbot.css";

const MiloChatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [welcomeVisible, setWelcomeVisible] = useState(true);
  const [history, setHistory] = useState([]);
  const [historyIndex, setHistoryIndex] = useState(-1);
  const [isTyping, setIsTyping] = useState(false);

  const [step, setStep] = useState(null); // Track conversation steps
  const [tripDetails, setTripDetails] = useState({}); // Store user trip details incrementally

  const apiBaseURL = "http://127.0.0.1:5001/";

  useEffect(() => {
    const initialMessages = [
      { sender: "bot", text: "Hi, I'm Milo, your personal travel assistant! Let's start planning your trip." },
    ];
    setMessages(initialMessages);
  }, []);

  const handleUserMessage = async () => {
    if (!input.trim()) return;

    const userMessage = input.trim();
    setMessages((prev) => [...prev, { sender: "user", text: userMessage }]);
    setHistory([userMessage, ...history]);
    setInput("");
    setWelcomeVisible(false);
    setIsTyping(true);

    let endpoint = "/MiloChatbot";
    let payload = { user_input: userMessage };

    if (step === "transport") {
      const transport = userMessage.toLowerCase();
      setTripDetails((prev) => ({ ...prev, mode_of_transport: transport }));

      if (transport === "bus" || transport === "train") {
        setMessages((prev) => [
          ...prev,
          { sender: "milo", text: "On day 2 and beyond, only cars can be selected for transport." },
        ]);
      }

      setMessages((prev) => [...prev, { sender: "milo", text: "What is your budget for this trip? (Minimum: 10,000)" }]);
      setStep("budget");
      setIsTyping(false);
      return;
    }

    if (step === "budget") {
      const budget = parseInt(userMessage, 10);
      if (isNaN(budget) || budget < 10000) {
        setMessages((prev) => [...prev, { sender: "milo", text: "The budget is too low. Please provide a budget above 10,000." }]);
        setIsTyping(false);
        return;
      }

      setTripDetails((prev) => ({ ...prev, budget }));
      setMessages((prev) => [...prev, { sender: "milo", text: "Do you prefer a hotel or an Airbnb for accommodation?" }]);
      setStep("accommodation");
      setIsTyping(false);
      return;
    }

    if (step === "accommodation") {
      const accommodation = userMessage.toLowerCase();
      if (!["hotel", "airbnb"].includes(accommodation)) {
        setMessages((prev) => [...prev, { sender: "milo", text: "Please choose either 'hotel' or 'Airbnb'." }]);
        setIsTyping(false);
        return;
      }

      setTripDetails((prev) => ({ ...prev, accommodation }));

      payload = {
        user_input: `Plan a trip from ${tripDetails.starting_city} to ${tripDetails.destination_city} for ${tripDetails.days} days`,
        starting_city: tripDetails.starting_city,
        destination_city: tripDetails.destination_city,
        days: tripDetails.days,
        mode_of_transport: tripDetails.mode_of_transport,
        budget: tripDetails.budget,
        accommodation: accommodation,
      };
      endpoint = "/generate-itinerary";
    }

    if (step === null && (/plan a trip|generate an itinerary|form a plan|create a plan|create a trip/i).test(userMessage)) {
      const userInputParts = userMessage.split(" ");
      const startingCity = userInputParts.includes("from")
        ? userInputParts[userInputParts.indexOf("from") + 1]
        : "Islamabad";
      const destinationCity = userInputParts.includes("to")
        ? userInputParts[userInputParts.indexOf("to") + 1]
        : "Lahore";
      const daysMatch = userMessage.match(/(\d+)\s+days/);
      const days = daysMatch ? parseInt(daysMatch[1], 10) : 3;

      setTripDetails({ starting_city: startingCity, destination_city: destinationCity, days });
      setMessages((prev) => [...prev, { sender: "milo", text: "What is your mode of transport (bus, train, or car)?" }]);
      setStep("transport");
      setIsTyping(false);
      return;
    }

    if ((/tell me about|give details on|what is|where is|give info on|give information on|provide details on|provide information on/i).test(userMessage)) {
      endpoint = "/retrieve-details";
    }

    if ((/top|best/i).test(userMessage)) {
      const match = userMessage.match(/top\s+(\d+)?\s*(hotels|restaurants|attractions|airbnbs?)\s+in\s+([\w\s]+)/i);
      if (match) {
        const numRecommendations = parseInt(match[1], 10) || 5;
        const category = match[2].toLowerCase();
        const city = match[3].trim().toLowerCase();

        endpoint = "/retrieve-top-items";
        payload = {
          user_input: userMessage,
          num_recommendations: numRecommendations,
          category,
          city,
        };
      } else {
        setMessages((prev) => [...prev, { sender: "milo", text: "Please specify what type of top information you need (e.g., 'top 5 hotels in Lahore')." }]);
        setIsTyping(false);
        return;
      }
    }

    try {
      const response = await fetch(`${apiBaseURL}${endpoint}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      const data = await response.json();

      let botResponse;

      if (endpoint === "/retrieve-details" && data.result) {
        botResponse = data.result.replace(/\n/g, "<br>");
      } else if (endpoint === "/retrieve-top-items" && data.result) {
        botResponse = data.result.replace(/\n/g, "<br>");
      } else if (endpoint === "/generate-itinerary" && data.response) {
        botResponse = data.response.replace(/\n/g, "<br>");
      } else {
        botResponse = "I'm here to assist you!";
      }

      setMessages((prev) => [...prev, { sender: "milo", text: botResponse }]);
    } catch (error) {
      console.error("Error communicating with backend:", error);
      setMessages((prev) => [
        ...prev,
        { sender: "milo", text: "Sorry, something went wrong. Please try again later." },
      ]);
    } finally {
      setIsTyping(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleUserMessage();
    } else if (e.key === "ArrowUp") {
      if (historyIndex < history.length - 1) {
        const newIndex = historyIndex + 1;
        setInput(history[newIndex]);
        setHistoryIndex(newIndex);
      }
    } else if (e.key === "ArrowDown") {
      if (historyIndex > 0) {
        const newIndex = historyIndex - 1;
        setInput(history[newIndex]);
        setHistoryIndex(newIndex);
      } else {
        setInput("");
        setHistoryIndex(-1);
      }
    }
  };

  useEffect(() => {
    const chatContainer = document.getElementById("chat-container");
    if (chatContainer) {
      chatContainer.scrollTop = chatContainer.scrollHeight;
    }
  }, [messages]);

  return (
    <div className="chatbot-container">
      <Navbar />
      {welcomeVisible && (
        <div className="welcome-message">
          <p>Welcome to a chat with Milo, your personal smart AI trip planner.</p>
          <p>Click on the input bar to start the chat.</p>
        </div>
      )}

      <div id="chat-container" className="chat-container">
        {messages.map((msg, index) => (
          <div key={index} className={`chat-bubble ${msg.sender}`}>
            <span>{msg.sender === "user" ? `You: ${msg.text}` : `Milo: ${msg.text}`}</span>
          </div>
        ))}
        {isTyping && (
          <div className="chat-bubble milo">
            <span>Milo is typing...</span>
          </div>
        )}
      </div>

      <div className="input-container">
        <input
          type="text"
          placeholder="Type your message here..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          onFocus={() => setWelcomeVisible(false)}
        />
        <button onClick={handleUserMessage}>Send</button>
      </div>
    </div>
  );
};

export default MiloChatbot;
