import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';
import logo from '../../assets/LOGO.png';

const Navbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [searchActive, setSearchActive] = useState(false);  // State to toggle search bar visibility
  const [query, setQuery] = useState('');  // State for storing search input
  const [recommendations, setRecommendations] = useState([]);  // State for storing search results

  const navigate = useNavigate();

  useEffect(() => {
    const checkAuthentication = () => {
      const token = localStorage.getItem('token');
      setIsAuthenticated(!!token);
    };

    checkAuthentication();
    window.addEventListener('storage', checkAuthentication);

    return () => {
      window.removeEventListener('storage', checkAuthentication);
    };
  }, []);

  const signOut = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsAuthenticated(false);
    navigate('/login');
  };

  // Fetch recommendations from the backend
  const fetchRecommendations = async (searchTerm) => {
    if (searchTerm) {
      const response = await fetch(`/api/search?query=${searchTerm}`);
      const data = await response.json();
      setRecommendations(data);  // Set the recommendations
    } else {
      setRecommendations([]);
    }
  };

  // Handle search input changes
  const handleSearchChange = (e) => {
    setQuery(e.target.value);
    fetchRecommendations(e.target.value);  // Trigger search on input change
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo">
          <img src={logo} alt="Logo" />
          <span>Voyaige</span>
        </Link>

        <ul className="navbar-menu">
          <li><Link to="/">Home</Link></li>
          <li><Link to="/tours">Tours</Link></li>
          <li><Link to="/hotels">Hotels</Link></li>
          <li><Link to="/eat-drink">Eat & Drink</Link></li>
        </ul>

        <div className="navbar-actions">
          <button onClick={() => setSearchActive(!searchActive)} className="search-icon">üîç</button>
          {isAuthenticated ? (
            <button onClick={signOut} className="auth-button">Log Out</button>
          ) : (
            <Link to="/login" className="auth-button">Sign In</Link>
          )}
        </div>
      </div>

      {/* Search Bar */}
      {searchActive && (
        <div className="search-bar">
          <input
            type="text"
            value={query}
            onChange={handleSearchChange}
            placeholder="Search for hotels, destinations..."
          />
          {/* Displaying only 3 recommendations */}
          <div className="recommendations">
            {recommendations.slice(0, 3).map((rec, index) => (
              <div key={index} className="recommendation-item">
                <span>{rec.name}</span>
              </div>
            ))}
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
