import React, { useState, useEffect } from "react";
import Navbar from "../NavBar/Navbar";
import "./MiloChatbot.css";

const MiloChatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [itineraryVisible, setItineraryVisible] = useState(false);
  const [itineraryContent, setItineraryContent] = useState("");
  const [itineraryTitle, setItineraryTitle] = useState("");

  const apiBaseURL = "http://127.0.0.1:5001/";

  useEffect(() => {
    setMessages([{ sender: "bot", text: "Hi, I'm Milo! Let's start planning your trip." }]);
  }, []);

  const handleUserMessage = async () => {
    if (!input.trim()) return;

    const userMessage = input.trim();
    setMessages((prev) => [...prev, { sender: "user", text: userMessage }]);
    setInput("");
    setIsTyping(true);

    let endpoint = "/MiloChatbot";
    let payload = { user_input: userMessage };

    if (/plan a trip|generate itinerary/i.test(userMessage)) {
      endpoint = "/generate-itinerary";
      payload = { user_input: userMessage };

      setMessages((prev) => [...prev, { sender: "milo", text: "Generating itinerary..." }]);
    }

    try {
      const response = await fetch(`${apiBaseURL}${endpoint}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      const data = await response.json();

      if (endpoint === "/generate-itinerary" && data.response) {
        setItineraryTitle("Itinerary");
        setItineraryContent(data.response);
        setItineraryVisible(true);
      } else {
        setMessages((prev) => [...prev, { sender: "milo", text: data.response || "I'm here to assist!" }]);
      }
    } catch (error) {
      console.error("Error:", error);
      setMessages((prev) => [...prev, { sender: "milo", text: "Something went wrong. Try again." }]);
    } finally {
      setIsTyping(false);
    }
  };

  const closeItinerary = () => {
    setItineraryVisible(false);
    setItineraryContent("");
  };

  return (
    <div className={`chatbot-container ${itineraryVisible ? "shift-left" : ""}`}>
      <Navbar />

      <div className="chat-container">
        {messages.map((msg, index) => (
          <div key={index} className={`chat-bubble ${msg.sender}`}>
            {msg.sender === "user" ? `You: ${msg.text}` : `Milo: ${msg.text}`}
          </div>
        ))}
        {isTyping && <div className="chat-bubble milo">Milo is typing...</div>}
      </div>

      {itineraryVisible && (
        <div className="itinerary-container">
          <button className="close-button" onClick={closeItinerary}>‚ùå</button>
          <h2>{itineraryTitle}</h2>
          <div className="itinerary-content">
            {itineraryContent.split("\n\n").map((day, index) => (
              <div key={index} className="itinerary-day">{day}</div>
            ))}
          </div>
        </div>
      )}

      <div className="input-container">
        <input
          type="text"
          placeholder="Type your message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleUserMessage()}
        />
        <button onClick={handleUserMessage}>Send</button>
      </div>
    </div>
  );
};

export default MiloChatbot;
