import React, { useState } from 'react';
import axios from 'axios';
import './MiloChatbot.css';
import Navbar from '../NavBar/Navbar';

const MiloChatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = async () => {
    if (input.trim()) {
      setMessages([...messages, { user: true, text: input }]);
      setInput('');

      try {
        const response = await axios.post('http://localhost:5001/chat', { message: input });
        setMessages((prev) => [...prev, { user: false, text: response.data.response }]);
      } catch (error) {
        console.error("Error connecting to the chatbot API", error);
      }
    }
  };

  const handleInputChange = (e) => setInput(e.target.value);

  return (
    <div className="milo-chatbot">
      <Navbar />
      <div className="chat-window">
        <div className="messages">
          {messages.map((msg, index) => (
            <div key={index} className={`message ${msg.user ? 'user' : 'milo'}`}>
              {msg.text}
            </div>
          ))}
        </div>
        <div className="input-area">
          <input
            type="text"
            value={input}
            onChange={handleInputChange}
            placeholder="Type your message..."
          />
          <button onClick={handleSend}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default MiloChatbot;
