import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaHotel, FaUtensils, FaSearch, FaSpinner, FaMapMarkerAlt, FaWalking } from 'react-icons/fa';
import logo from '../../assets/LOGO.png';
import './Navbar.css';
import axios from 'axios';

const Navbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState(null);
  const [searchOpen, setSearchOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [backgroundGray, setBackgroundGray] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const checkAuthentication = () => {
      const token = localStorage.getItem('token');
      setIsAuthenticated(!!token);
    };

    checkAuthentication();
    window.addEventListener('storage', checkAuthentication);

    return () => {
      window.removeEventListener('storage', checkAuthentication);
    };
  }, []);

  useEffect(() => {
    setBackgroundGray(searchOpen);
  }, [searchOpen]);

  const signOut = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsAuthenticated(false);
    navigate('/login');
  };

  const handleSearch = async (query) => {
    setSearchQuery(query);
    if (query.length >= 2) {
      setIsLoading(true);
      setError(null);
      try {
        const { data } = await axios.get(`http://localhost:5000/api/search`, {
          params: { query }
        });
        setSearchResults({
          city: {
            name: query,
            path: `/city/${query.toLowerCase()}`,
          },
          hotels: data.hotels || [],
          restaurants: data.restaurants || [],
          thingsToDo: data.attractions || []
        });
      } catch (error) {
        console.error('Error fetching search results:', error);
        setError('An error occurred while searching. Please try again.');
      } finally {
        setIsLoading(false);
      }
    } else {
      setSearchResults(null);
    }
  };

  const handleCityClick = (cityName) => {
    navigate(`/city/${cityName.toLowerCase()}`, { state: { cityName } });
    setSearchOpen(false);
    setSearchQuery('');
  };

  const handleSectionClick = (cityName, section, sectionId) => {
    navigate(`/city/${cityName.toLowerCase()}`, {
      state: { cityName, scrollTo: section }
    });
    setSearchOpen(false);
    setSearchQuery('');
  };

  return (
    <nav className={`navbaar ${backgroundGray ? 'gray-background' : ''}`}>
      <div className="navbar-container">
        <Link to="/" className="navbar-logo">
          <img src={logo} alt="Logo" />
          <span>Voyaige</span>
        </Link>
        <ul className="navbar-menu">
          <li><Link to="/">Home</Link></li>
          <li><Link to="/tours">Tours</Link></li>
          <li><Link to="/hotels">Hotels</Link></li>
          <li><Link to="/restaurants">Eat & Drink</Link></li>
        </ul>
        <div className="navbar-actions">
          <button onClick={() => setSearchOpen(!searchOpen)} className="search-button2">
            <FaSearch />
          </button>
          {isAuthenticated ? (
            <button className="auth-button" onClick={signOut}>Log Out</button>
          ) : (
            <Link className="auth-button" to="/login">Sign In</Link>
          )}
        </div>
      </div>

      {searchOpen && (
        <div className="search-box">
          <div className="search-input-container">
            <FaSearch className="search-icon" />
            <input
              type="text"
              placeholder="Search cities..."
              value={searchQuery}
              onChange={(e) => handleSearch(e.target.value)}
              className="search-input"
            />
          </div>

          {isLoading && <div className="loading"><FaSpinner className="spinner" /></div>}
          {error && <p className="error-message">{error}</p>}
          
          {searchResults && searchQuery && (
            <div className="search-recommendations">
              {/* City Result */}
              <div className="recommendation-item" onClick={() => handleCityClick(searchResults.city.name)}>
                <FaMapMarkerAlt className="recommendation-icon" />
                <div className="recommendation-content">
                  <div className="recommendation-title">{searchResults.city.name}</div>
                  <div className="recommendation-subtitle">City</div>
                </div>
              </div>

              {/* Hotels */}
              <div 
                className="recommendation-item"
                onClick={() => handleSectionClick(searchResults.city.name, 'hotels')}
              >
                <FaHotel className="recommendation-icon" />
                <div className="recommendation-content">
                  <div className="recommendation-title">Hotels</div>
                  <div className="recommendation-subtitle">in {searchResults.city.name}</div>
                </div>
              </div>

              {/* Things to Do */}
              <div 
                className="recommendation-item"
                onClick={() => handleSectionClick(searchResults.city.name, 'attractions')}
              >
                <FaWalking className="recommendation-icon" />
                <div className="recommendation-content">
                  <div className="recommendation-title">Things to Do</div>
                  <div className="recommendation-subtitle">in {searchResults.city.name}</div>
                </div>
              </div>

              {/* Restaurants */}
              <div 
                className="recommendation-item"
                onClick={() => handleSectionClick(searchResults.city.name, 'restaurants')}
              >
                <FaUtensils className="recommendation-icon" />
                <div className="recommendation-content">
                  <div className="recommendation-title">Restaurants</div>
                  <div className="recommendation-subtitle">in {searchResults.city.name}</div>
                </div>
              </div>
            </div>
          )}
        </div>
      )}
    </nav>
  );
};

export default Navbar;
