import React, { useState } from 'react';
import NavBar from "../NavBar/Navbar";
import './MiloChatbot.css';

const MiloChatbot = () => {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSendMessage = async () => {
    if (!userInput.trim()) return; // Prevent sending empty messages

    // Add user message
    setMessages((prevMessages) => [
      ...prevMessages,
      { text: userInput, sender: 'user' },
    ]);

    setIsLoading(true);
    setUserInput('');

    // Simulate bot response (you can replace this with your actual API call)
    setTimeout(() => {
      setMessages((prevMessages) => [
        ...prevMessages,
        { text: 'I am the bot, how can I assist you?', sender: 'bot' },
      ]);
      setIsLoading(false);
    }, 1000); // Simulated delay for bot response
  };

  return (
    <NavBar> 
      <div className="chat-container">
        {/* Chat history area */}
        <div className="chat-history">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`chat-bubble ${message.sender === 'user' ? 'user-bubble' : 'bot-bubble'}`}
            >
              {message.text}
            </div>
          ))}
          {isLoading && (
            <div className="chat-bubble bot-bubble">...</div>
          )}
        </div>

        {/* Chat input area */}
        <div className="chat-input-container">
          <input
            type="text"
            className="chat-input"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            placeholder="Type a message..."
          />
          <button
            className="send-button"
            onClick={handleSendMessage}
            disabled={isLoading}
          >
            Send
          </button>
        </div>
      </div>
    </NavBar> 
  );
};

export default MiloChatbot;
