import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Navbar from '../../NavBar/Navbar';
import './UserCustomization.css';

const UserCustomization = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { customizationData, bookingData } = location.state || {};

  const {
    startingPoint = '',
    destination = '',
    startDate = '',
    endDate = '',
    guests = 1
  } = customizationData || {};

  const [numberOfDays, setNumberOfDays] = useState(0);
  const [dayDetails, setDayDetails] = useState({});

  useEffect(() => {
    if (startDate && endDate) {
      const start = new Date(startDate);
      const end = new Date(endDate);
      const daysDiff = Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
      setNumberOfDays(daysDiff > 0 ? daysDiff : 0);
    }
  }, [startDate, endDate]);

  useEffect(() => {
    const savedDetails = localStorage.getItem('dayDetails');
    if (savedDetails) {
      try {
        setDayDetails(JSON.parse(savedDetails));
      } catch (error) {
        console.error("Error parsing day details from localStorage:", error);
        localStorage.removeItem('dayDetails');
      }
    }
  }, []);

  useEffect(() => {
    if (Object.keys(dayDetails).length > 0) {
      localStorage.setItem('dayDetails', JSON.stringify(dayDetails));
    }
  }, [dayDetails]);

  useEffect(() => {
    if (bookingData) {
      const { dayIndex, selectedHotel, selectedTransport } = bookingData;
      setDayDetails(prev => {
        const updatedDetails = { ...prev, [dayIndex]: { hotel: selectedHotel, transport: selectedTransport } };
        localStorage.setItem('dayDetails', JSON.stringify(updatedDetails));
        return updatedDetails;
      });
    }
  }, [bookingData]);

  const handleEditClick = (day) => {
    navigate('/bookoption', { state: { day, customizationData, currentDetails: dayDetails[day] || {} } });
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { weekday: 'short', month: 'short', day: 'numeric', year: 'numeric' });
  };

  return (
    <div className="customization-container">
      <Navbar />
      <h2 className="customization-title">Your Trip Details</h2>

      <div className="trip-summary">
        <h3>Trip Summary</h3>
        <div className="summary-details">
          <p><strong>From:</strong> {startingPoint}</p>
          <p><strong>To:</strong> {destination}</p>
          <p><strong>Start Date:</strong> {formatDate(startDate)}</p>
          <p><strong>End Date:</strong> {formatDate(endDate)}</p>
          <p><strong>Guests:</strong> {guests} {guests === 1 ? 'Person' : 'Persons'}</p>
          <p><strong>Duration:</strong> {numberOfDays} {numberOfDays === 1 ? 'Day' : 'Days'}</p>
        </div>
      </div>

      {numberOfDays > 0 && (
        <div className="days-section">
          <h3>Day-by-Day Itinerary</h3>
          {[...Array(numberOfDays)].map((_, index) => {
            const dayNumber = index + 1;
            const details = dayDetails[dayNumber] || {};
            const dayDate = new Date(startDate);
            dayDate.setDate(dayDate.getDate() + index);

            return (
              <div key={index} className="day-card">
                <div className="day-header">
                  <span className="day-number">DAY {dayNumber}</span>
                  <span className="day-date">{formatDate(dayDate)}</span>
                  <button className="edit-button" onClick={() => handleEditClick(dayNumber)}>Edit</button>
                </div>

                <div className="day-line"></div>

                {details.hotel || details.transport ? (
                  <div className="booking-details-container">
                    {details.hotel && (
                      <div className="booking-details hotel-details">
                        <h4>Hotel</h4>
                        <p><strong>Name:</strong> {details.hotel.name || 'N/A'}</p>
                        <p><strong>Price:</strong> {details.hotel.price || 'N/A'} PKR</p>
                        <p><strong>Rating:</strong> {details.hotel.rating || 'N/A'}/5</p>
                      </div>
                    )}
                    {details.transport && (
                      <div className="booking-details transport-details">
                        <h4>Transport</h4>
                        <p><strong>Type:</strong> {details.transport.type || 'N/A'}</p>
                        <p><strong>Price:</strong> {details.transport.price || 'N/A'} PKR</p>
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="no-booking-details">
                    <p>No details added yet for Day {dayNumber}</p>
                    <button className="add-details-button" onClick={() => handleEditClick(dayNumber)}>Add Details</button>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      )}

      <div className="action-buttons">
        <button className="back-button" onClick={() => navigate(-1)}>Back</button>
        <button className="save-button" onClick={() => alert('Your itinerary has been saved!')}>Save Itinerary</button>
      </div>
    </div>
  );
};

export default UserCustomization;
