// LoginForm.js
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { FaUser, FaLock, FaPlane, FaExclamationCircle } from 'react-icons/fa';
import axios from 'axios';
import './Login.css';

const LoginForm = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();

  const validateField = (name, value) => {
    switch (name) {
      case 'email':
        if (!value) return 'Email is required';
        if (!value.includes('@') || !value.includes('.')) return 'Invalid email format';
        return '';
      case 'password':
        if (!value) return 'Password is required';
        if (value.length < 8) return 'Password must be at least 8 characters';
        return '';
      default:
        return '';
    }
  };

  const onChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    setErrors(prev => ({ ...prev, [name]: validateField(name, value) }));
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    const emailError = validateField('email', formData.email);
    const passwordError = validateField('password', formData.password);
    
    if (!emailError && !passwordError) {
      setIsSubmitting(true);
      try {
        const res = await axios.post('http://localhost:5000/api/users/login', formData);
        localStorage.setItem('token', res.data.token);
        localStorage.setItem('user', JSON.stringify(res.data.user));
        window.dispatchEvent(new Event('storage'));
        navigate('/');
      } catch (err) {
        setErrors({ submit: 'Login failed. Please check your credentials.' });
      } finally {
        setIsSubmitting(false);
      }
    } else {
      setErrors({ email: emailError, password: passwordError });
    }
  };

  return (
    <div className="login-page">
      <nav className="navbar">
        <Link to="/" className="logo">
          <FaPlane className="plane-icon" /> Voyaige
        </Link>
      </nav>

      <div className="login-container">
        {/* Left Section */}
        <div className="login-left">
          <h1>Welcome to Voyaige</h1>
          <p>Your personalized trip planner.</p>
        </div>

        {/* Right Section - Login Form */}
        <div className="login-right">
          <h2>Welcome Back!</h2>
          {errors.submit && (
            <div className="global-error">
              <FaExclamationCircle /> {errors.submit}
            </div>
          )}

          <form onSubmit={onSubmit}>
            <div className={`input-group ${errors.email ? 'input-error' : ''}`}>
              <FaUser className="icon" />
              <input type="text" name="email" value={formData.email} onChange={onChange} placeholder="Email" />
              {errors.email && <div className="error-tooltip"><FaExclamationCircle /> {errors.email}</div>}
            </div>

            <div className={`input-group ${errors.password ? 'input-error' : ''}`}>
              <FaLock className="icon" />
              <input type="password" name="password" value={formData.password} onChange={onChange} placeholder="Password" />
              {errors.password && <div className="error-tooltip"><FaExclamationCircle /> {errors.password}</div>}
            </div>

            <button type="submit" className="submit-btn" disabled={isSubmitting}>
              {isSubmitting ? 'Logging In...' : 'Log In'}
            </button>
          </form>

          <p className="switch-form">
            Don't have an account? <Link to="/signup">Sign Up</Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
