import React, { useState } from 'react';
import './AiBanner.css';

const AiBanner = () => {
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  const handleTryClick = () => {
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    setIsPopupOpen(false);
  };

  return (
    <div className={`ai-trip-banner ${isPopupOpen ? 'blur-background' : ''}`}>
      <div className="banner-content">
        <div className="text-content">
          <div className="ai-badge">
            Powered by AI <span className="beta-tag">BETA</span>
          </div>
          <h1 className="aimain-title">AI trip builder gets you out there</h1>
          <p className="aisubtitle">Get a whole getaway's worth of ideas made for you—ready in seconds.</p>
          <button className="try-button" onClick={handleTryClick}>
            <span className="icon">✦</span> Try it
          </button>
        </div>
        <div className="image-container">
          <img
            src="https://img.eurohike.at/w_314,h_235,q_80,v_49f23c,hash_7fe451/ddcijcnkv/image/upload/v1683199297/eurohike/touren/oesterreich/almwandern-salzkammergut-fuer-familien/neu/eurohike-wanderreise-salzkammergut-genneralm-familie1.jpg"
            alt="Family hiking together"
            className="banner-image"
          />
        </div>
      </div>

      {isPopupOpen && (
        <div className="popup-container">
          <div className="popup-content">
            <button className="close-popup" onClick={closePopup}>
              ✖
            </button>
            <div className="popup-options">
              <div className="option-left" onClick={() => window.location.href = '/ai-chatbot-plan'}>
                Plan with AI Chatbot
              </div>
              <div className="option-right" onClick={() => window.location.href = '/form-based-plan'}>
                Plan with Form-Based Recommendation
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AiBanner;
