import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Calendar, User, Mail, Printer, Home, Phone, Clock, MapPin, Hotel, Car, Camera } from 'lucide-react';
import './Cart.css';
import Navbar from '../NavBar/Navbar';

const Cart = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [receiptData, setReceiptData] = useState(null);
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Get data from location state passed by checkout page
    if (location.state?.tripData && location.state?.paymentDetails) {
      setReceiptData({
        tripData: location.state.tripData,
        paymentDetails: location.state.paymentDetails
      });
      
      // In a real app, you would fetch user data from context/redux/api
      // This is a mock for demonstration
      setUserData({
        name: "Ms Milo Khan",
        bookingId: "TRP-" + Math.floor(10000 + Math.random() * 90000),
        phone: "0333-7777777",
        email: "m.khan@outlook.com",
        date: new Date().toLocaleDateString('en-US', { year: 'numeric', month: '2-digit', day: '2-digit' })
      });
    } else {
      // If no data, set some default data or redirect
      console.warn("No trip data found in location state");
    }
  }, [location.state]);

  const handlePrint = () => {
    window.print();
  };

  const handleEmail = () => {
    // In a real application, this would send an API request to email the receipt
    alert("Receipt will be emailed to " + userData?.email);
  };

  const handleBackToHome = () => {
    navigate('/');
  };

  // Show loading state if data is still loading
  if (!receiptData || !userData) {
    return (
      <div className="itinerary-card loading-state">
        <Navbar />
        <div className="loading-spinner"></div>
        <p>Loading your receipt...</p>
      </div>
    );
  }

  const { tripData, paymentDetails } = receiptData;
  const { summary, days } = tripData;

  return (
    <div className="itinerary-card receipt-page">
      <Navbar />
      <div className="header receipt-header">
        <div className="header-top">
          <h1>Trip Receipt & Itinerary</h1>
          <div className="payment-badge">
            <span className="payment-status">Payment Status: {paymentDetails.status}</span>
            <span className="payment-date">Date: {new Date(paymentDetails.date).toLocaleDateString()}</span>
          </div>
        </div>

        <div className="trip-summary">
          {summary && (
            <>
              <div className="summary-destinations">
                <span className="from">{summary.from}</span>
                <span className="separator">→</span>
                <span className="to">{summary.to}</span>
              </div>
              <div className="summary-details">
                <div className="summary-item">
                  <Calendar size={16} />
                  <span>
                    {new Date(summary.startDate).toLocaleDateString('en-US', { 
                      month: 'short', day: 'numeric' 
                    })} - {new Date(summary.endDate).toLocaleDateString('en-US', { 
                      month: 'short', day: 'numeric', year: 'numeric' 
                    })}
                  </span>
                </div>
                <div className="summary-item">
                  <User size={16} />
                  <span>{summary.guests} {summary.guests > 1 ? 'Guests' : 'Guest'} • {summary.duration} {summary.duration > 1 ? 'Days' : 'Day'}</span>
                </div>
              </div>
            </>
          )}
        </div>

        <div className="user-info">
          <div className="info-column">
            <div className="info-row">
              <User size={16} />
              <span>Name: {userData.name}</span>
            </div>
            <div className="info-row">
              <Phone size={16} />
              <span>Phone: {userData.phone}</span>
            </div>
          </div>
          <div className="info-column">
            <div className="info-row">
              <Mail size={16} />
              <span>Email: {userData.email}</span>
            </div>
            <div className="info-row">
              <span className="booking-id">Booking ID: {userData.bookingId}</span>
            </div>
          </div>
        </div>
      </div>

      <div className="receipt-summary">
        <h2>Payment Summary</h2>
        <div className="receipt-grid">
          <div className="receipt-item">
            <span>Amount Paid:</span>
            <span className="amount">{paymentDetails.amount.toLocaleString()}</span>
          </div>
          <div className="receipt-item">
            <span>Payment Date:</span>
            <span>{new Date(paymentDetails.date).toLocaleDateString()}</span>
          </div>
          <div className="receipt-item">
            <span>Status:</span>
            <span className="status">{paymentDetails.status}</span>
          </div>
        </div>
      </div>

      <div className="itinerary-content">
        <h2>Your Trip Itinerary</h2>
        
        {days && days.map((day, index) => (
          <div key={index} className="day-section">
            <div className="day-header">
              <h3>DAY {day.dayNumber}: {day.title}</h3>
              <span className="day-date">
                {day.date ? new Date(day.date).toLocaleDateString('en-US', { 
                  weekday: 'short', month: 'short', day: 'numeric' 
                }) : `Day ${day.dayNumber}`}
              </span>
            </div>
            
            {/* Accommodations */}
            {day.accommodation && (
              <div className="itinerary-category">
                <div className="category-header">
                  <Hotel size={18} />
                  <h4>Accommodation</h4>
                </div>
                <div className="category-content">
                  <div className="detail-item">
                    <span className="detail-name">{day.accommodation.name}</span>
                    <span className="detail-info">{day.accommodation.roomType}</span>
                    <span className="detail-address">
                      <MapPin size={14} /> {day.accommodation.address || 'Address information not available'}
                    </span>
                    {day.accommodation.checkIn && (
                      <div className="check-times">
                        <span><Clock size={14} /> Check-in: {day.accommodation.checkIn}</span>
                        <span><Clock size={14} /> Check-out: {day.accommodation.checkOut}</span>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            )}
            
            {/* Transportation */}
            {day.transportation && (
              <div className="itinerary-category">
                <div className="category-header">
                  <Car size={18} />
                  <h4>Transportation</h4>
                </div>
                <div className="category-content">
                  <div className="detail-item">
                    <span className="detail-name">{day.transportation.type}</span>
                    {day.transportation.details && (
                      <span className="detail-info">{day.transportation.details}</span>
                    )}
                    {day.transportation.from && day.transportation.to && (
                      <div className="transport-route">
                        <span>{day.transportation.from}</span>
                        <span className="route-arrow">→</span>
                        <span>{day.transportation.to}</span>
                      </div>
                    )}
                    {day.transportation.departureTime && (
                      <div className="transport-times">
                        <span><Clock size={14} /> Departure: {day.transportation.departureTime}</span>
                        {day.transportation.arrivalTime && (
                          <span><Clock size={14} /> Arrival: {day.transportation.arrivalTime}</span>
                        )}
                      </div>
                    )}
                  </div>
                </div>
              </div>
            )}
            
            {/* Attractions/Activities */}
            {day.attractions && day.attractions.length > 0 && (
              <div className="itinerary-category">
                <div className="category-header">
                  <Camera size={18} />
                  <h4>Attractions & Activities</h4>
                </div>
                <div className="category-content">
                  {day.attractions.map((attraction, idx) => (
                    <div key={idx} className="detail-item">
                      <div className="timeline-item">
                        {attraction.time && <span className="time">{attraction.time}</span>}
                        <div className="timeline-content">
                          <span className="activity-name">{attraction.name}</span>
                          {attraction.description && (
                            <span className="activity-description">{attraction.description}</span>
                          )}
                          {attraction.location && (
                            <span className="activity-location">
                              <MapPin size={14} /> {attraction.location}
                            </span>
                          )}
                        </div>
                      </div>
                      {idx < day.attractions.length - 1 && <div className="timeline-connector"></div>}
                    </div>
                  ))}
                </div>
              </div>
            )}
            
            {/* Schedule (if exists in the data structure) */}
            {day.schedule && day.schedule.length > 0 && (
              <div className="itinerary-category">
                <div className="category-header">
                  <Clock size={18} />
                  <h4>Schedule</h4>
                </div>
                <div className="category-content timeline">
                  {day.schedule.map((item, idx) => (
                    <div key={idx} className="timeline-item">
                      <span className="time">{item.time}</span>
                      <div className="timeline-content">
                        <span className="activity">{item.activity}</span>
                        <span className="location">{item.location}</span>
                      </div>
                      {idx < day.schedule.length - 1 && <div className="timeline-connector"></div>}
                    </div>
                  ))}
                </div>
              </div>
            )}
            
            {/* Any other day details */}
            {day.notes && (
              <div className="day-notes">
                <h4>Notes</h4>
                <p>{day.notes}</p>
              </div>
            )}
          </div>
        ))}
      </div>

      <div className="cost-summary">
        <h2>Cost Breakdown</h2>
        <div className="cost-items">
          {days && days.map((day, index) => (
            <div key={index} className="cost-item">
              <span>Day {day.dayNumber}: {day.title}</span>
              <span className="cost">{day.totalCost ? day.totalCost.toLocaleString() : '0'}</span>
            </div>
          ))}
          
          <div className="cost-divider"></div>
          
          <div className="subtotal cost-item">
            <span>Sub-total</span>
            <span className="cost">{(days?.reduce((sum, day) => sum + (day.totalCost || 0), 0)).toLocaleString()}</span>
          </div>
          
          {tripData.discountPercentage > 0 && (
            <div className="discount cost-item">
              <span>Points Discount ({tripData.discountPercentage}%)</span>
              <span className="cost-discount">
                -{((days?.reduce((sum, day) => sum + (day.totalCost || 0), 0) * tripData.discountPercentage / 100)).toLocaleString()}
              </span>
            </div>
          )}
          
          <div className="total cost-item">
            <span>Total Amount</span>
            <span className="cost-total">{paymentDetails.amount.toLocaleString()}</span>
          </div>
        </div>
      </div>

      <div className="button-group">
        <button className="bttn email-bttn" onClick={handleEmail}>
          <Mail size={16} /> EMAIL ME
        </button>
        <button className="bttn print-bttn" onClick={handlePrint}>
          <Printer size={16} /> PRINT COPY
        </button>
        <button className="bttn home-bttn" onClick={handleBackToHome}>
          <Home size={16} /> BACK TO HOMEPAGE
        </button>
      </div>
    </div>
  );
};

export default Cart;
